INSERT INTO [t9624] VALUES (1,'ركع ركعتين حين سمع أذان المغرب قبل الصلاة، وأنا أريد أن أمنعه، فقال عقبة بن عامر: «إنا قد','4','0','0');
INSERT INTO [t9624] VALUES (2,'أما ما ذكرت أنكم بأرض أهل الكتاب تأكلون في آنيتهم، فإن وجدتم غير آنيتهم فلا تأكلوا فيها،','5','0','0');
INSERT INTO [t9624] VALUES (3,'صلى رسول الله صلى الله عليه وسلم العشاء، ثم صلى ثمان ركعات قائما، وركعتين جالسا، وركعتين','6','0','0');
INSERT INTO [t9624] VALUES (4,'الدنيا حلوة خضرة، وإن رجالا سيخوضون في مال الله ورسوله بغير حق، لهم النار يوم القيامة» ،','8','0','0');
INSERT INTO [t9624] VALUES (5,'الله لا يستحيي من الحق، لا تأتوا النساء في أدبارهن» ، رواه الإمام أحمد، عن عبد الله بن','10','0','0');
INSERT INTO [t9624] VALUES (6,'من بلغه معروف من أخيه من غير مسألة ولا إشراف، فليقبله ولا يرده، فإنما هو رزق ساقه الله','10','0','0');
INSERT INTO [t9624] VALUES (7,'ليلة أسري بي، مررت بإبراهيم صلى الله عليه وسلم فقال: يا جبريل، من هذا معك؟ ، قال: محمد،','11','0','0');
INSERT INTO [t9624] VALUES (8,'ليلة أسري به، مر على إبراهيم خليل الرحمن، فقال إبراهيم عليه السلام: يا جبريل من هذا الذي','12','0','0');
INSERT INTO [t9624] VALUES (9,'غدوة في سبيل الله أو روحة خير مما طلعت عليه الشمس وغربت» ، رواه الإمام أحمد، عن المقرئ.','13','0','0');
INSERT INTO [t9624] VALUES (10,'الله لعن الخمر، وعاصرها، ومعتصرها، وشاربها، وحاملها، والمحمولة إليه، وبائعها، ومبتاعها،','14','0','0');
INSERT INTO [t9624] VALUES (11,'لا تمنعوا النساء حظوظهن من المساجد إذا استأذنوكم» ، فقال بلال: والله لنمنعهن، فقال عبد','14','0','0');
INSERT INTO [t9624] VALUES (12,'إني أعزل عن امرأتي، فقال: «لم» ؟ ، فقال: شفقا على ولدها، فقال: «إن كان ذلك فلا، ما ضر ذلك','16','0','0');
INSERT INTO [t9624] VALUES (13,'إن النار عدو، فاحذروها» ، قال: فكان ابن عمر يتبع نيران أهله، فيطفئها قبل أن يبيت. لفظهما','16','0','0');
INSERT INTO [t9624] VALUES (14,'خير الأصحاب عند الله جل وعز خيرهم لصاحبه، وخير الجيران عند الله جل وعز خيرهم لجاره» . لفظهم','18','0','0');
INSERT INTO [t9624] VALUES (15,'إن قلوب بني آدم كلها بين أصبعين من أصابع الرحمن كقلب واحد، يصرفه حيث يشاء» ، ثم قال رسول','19','0','0');
INSERT INTO [t9624] VALUES (16,'" قبرنا مع رسول الله صلى الله عليه وسلم رجلا، فلما رجعنا وحاذينا بابه، إذا هو بامرأة مقبلة','20','0','0');
INSERT INTO [t9624] VALUES (17,'ما من غازية تغزوا في سبيل الله، فيصيبون غنيمة، إلا تعجلوا ثلثي أجرهم، ويبقى لهم الثلث،','21','0','0');
INSERT INTO [t9624] VALUES (18,'قدر الله جل وعز المقادير قبل أن يخلق السموات والأرض بخمسين ألف سنة» . رواه الإمام أحمد، عن','22','0','0');
INSERT INTO [t9624] VALUES (19,'ما أبالي ما أتيت، أو ما ركبت، إذا ما تعلقت تميمة، أو شربت ترياقا، أو قلت الشعر من قبل','23','0','0');
INSERT INTO [t9624] VALUES (20,'هل تدرون أول من يدخل الجنة» ؟ ، قالوا: الله ورسوله أعلم، قال: " فقراء المهاجرين الذين يتقى','23','0','0');
INSERT INTO [t9624] VALUES (21,'سيكون في أمتي رجال يركبون نساءهم على سروج، كأشباه الرجال، كاسيات عاريات، على رءوسهن','24','0','0');
INSERT INTO [t9624] VALUES (22,'سيكون آخر أمتي نساء كاسيات عاريات، على رءوسهن كأسنمة البخت، العنوهن، فإنهن','25','0','0');
INSERT INTO [t9624] VALUES (23,'الصلاة، فقال: «من حافظ عليها كانت له نورا وبرهانا، ومن لم يحافظ عليها لم يكن له نور ولا','25','0','0');
INSERT INTO [t9624] VALUES (24,'أقرئني، قال: «أقرؤك من ذوات الحواميم» ، قال: يا رسول الله، ثقل لساني وغلظ كبدي، قال: «أقرؤك','26','0','0');
INSERT INTO [t9624] VALUES (25,'سلوا الله الوسيلة، فإنها منزل في الجنة، لا تنبغي إلا لعبد من عباد الله، وأرجو أن أكون أنا','27','0','0');
INSERT INTO [t9624] VALUES (26,'فمسح برأسه ودعا له» ، وكان يضحي بالشاة الواحدة عن جميع أهله "، رواه الإمام أحمد، عن عبد الله','27','0','0');
INSERT INTO [t9624] VALUES (27,'خير الصدقة ما كان عن ظهر غنى، واليد العليا خير من اليد السفلى، وابدأ بمن تعول» ، فقال: من','28','0','0');
INSERT INTO [t9624] VALUES (28,'خذ عليك من سلاحك» ؟ ، قال: ولبست ثيابي وسلاحي، ثم أقبلت إلى رسول الله صلى الله عليه وسلم،','29','0','0');
INSERT INTO [t9624] VALUES (29,'ما أبعد هديكم من هدي نبيكم صلى الله عليه وسلم، أما هو فكان أزهد الناس في الدنيا، وأما','29','0','0');
INSERT INTO [t9624] VALUES (30,'{فسبح باسم ربك العظيم} [الواقعة: ٧٤] ، قال لنا رسول الله صلى الله عليه وسلم: " اجعلوها في','31','0','0');
INSERT INTO [t9624] VALUES (31,'لو كان بعدي نبي لكان عمر» . لفظ الطبراني. وقال القطيعي: عن عقبة بن عامر الجهني، قال: سمعت','32','0','0');
INSERT INTO [t9624] VALUES (32,'لا يصيب المؤمن هم ولا حزن ولا وصب ولا أذى، إلا كفر الله جل وعز عنه» ، رواه الإمام أحمد، عن','33','0','0');
INSERT INTO [t9624] VALUES (33,'أيكم يحب أن يغدو إلى بطحان أو العقيق، فيأتي كل يوم بناقتين كوماوين زهراوين فيأخذهما،','34','0','0');
INSERT INTO [t9624] VALUES (34,'تعلموا كتاب الله واقتنوه وتعاهدوه وتغنوا به، فوالذي نفسي بيده لهو أشد تفصيا من صدور','34','0','0');
INSERT INTO [t9624] VALUES (35,'أهل اليمين أرق وألين أفئدة، وأسمع طاعة» ، رواه الإمام أحمد، عن المقرئ، وفيه «وأنجع','35','0','0');
INSERT INTO [t9624] VALUES (36,'لن تقرأ سورة أحب إلى الله ولا أبلغ عنده من قل أعوذ برب الفلق» ، رواه الإمام أحمد، عن','35','0','0');
INSERT INTO [t9624] VALUES (37,'لا تخيفوا الأنفس بعد أمنها» ، قالوا: يا رسول الله، وما ذاك؟ قال: «الدين» ، رواه الإمام أحمد،','36','0','0');
INSERT INTO [t9624] VALUES (38,'من مات على مرتبة من هذه المراتب بعث عليها يوم القيامة» ، رواه الإمام أحمد، عن','36','0','0');
INSERT INTO [t9624] VALUES (39,'ثلاثة لا تسأل عنهم: رجل فارق الجماعة وعصى إمامه ومات عاصيا، وأمة أو عبد أبق من سيده فمات،','37','0','0');
INSERT INTO [t9624] VALUES (40,'ثلاثة لا تسأل عنهم: رجل ينازع الله رداءه، فإن رداءه الكبرياء وإزاره العزة، ورجل شك في أمر','37','0','0');
INSERT INTO [t9624] VALUES (41,'إذا صلى أحدكم فليبدأ بتمجيد ربه والثناء عليه، ثم ليصل على النبي صلى الله عليه وسلم، ثم','38','0','0');
INSERT INTO [t9624] VALUES (42,'إذا صلى بالناس، يخر رجال من قامتهم في صلاتهم لما بهم من الخصاصة، وهم أصحاب الصفة، حتى','38','0','0');
INSERT INTO [t9624] VALUES (43,'إذا صلى يخر رجال من قامتهم لما بهم من الجهد، وكانوا أصحاب الصفة، حتى يقول الأعراب: إن','39','0','0');
INSERT INTO [t9624] VALUES (44,'يسلم الراكب على القائم، والقليل على الكثير» ، رواه الإمام أحمد، عن','39','0','0');
INSERT INTO [t9624] VALUES (45,'طوبى لمن هدي إلى الإسلام، وكان عيشه كفافا وقنع به» ، رواه الإمام أحمد، عن','40','0','0');
INSERT INTO [t9624] VALUES (46,'إني لأحبك» ، فقال له معاذ: بأبي أنت وأمي يا رسول الله، وأنا والله أحبك، فقال: " أوصيك يا','40','0','0');
INSERT INTO [t9624] VALUES (47,'من حمل من أمتي دينا، ثم جهد في قضائه فمات قبل أن يقضيه فأنا وليه» ، رواه الإمام أحمد، عن','41','0','0');
INSERT INTO [t9624] VALUES (48,'لا يحل لأحد أن يضرب أحدا فوق عشرة أسواط، إلا في حد من حدود الله جل وعز» ، رواه الإمام أحمد،','42','0','0');
INSERT INTO [t9624] VALUES (49,'من أكل طعاما فقال: الحمد لله الذي أطعمني هذا ورزقنيه من غير حول مني ولا قوة، غفر الله له','43','0','0');
INSERT INTO [t9624] VALUES (50,'نهى عن الحبوة يوم الجمعة والإمام يخطب» ، رواه الإمام أحمد بن المقرئ','44','0','0');
INSERT INTO [t9624] VALUES (51,'من ترك اللباس وهو يقدر عليه تواضعا لله عز وجل، دعاه الله على رءوس الخلائق يوم القيامة،','44','0','0');
INSERT INTO [t9624] VALUES (52,'من قتل معاهدا في غير كنهه، حرم الله عليه الجنة» ، رواه الإمام أحمد، عن','45','0','0');
INSERT INTO [t9624] VALUES (53,'أفرى الفرى من تقول علي ما لم أقل، ومن يري عينيه في المنام ما لم تر، ومن ادعى إلى غير','45','0','0');
INSERT INTO [t9624] VALUES (54,'من هجر أخاه سنة، فقد سفك دمه» ، رواه الإمام أحمد، عن المقرئ','46','0','0');
INSERT INTO [t9624] VALUES (55,'من قام مقام رياء وسمعة راءى الله به يوم القيامة وسمع» ، رواه الإمام أحمد، عن','47','0','0');
INSERT INTO [t9624] VALUES (56,'من صلى أربع ركعات قبل الظهر، وأربعا بعدها، حرمه الله على النار» ، رواه الإمام أحمد، عن','47','0','0');
INSERT INTO [t9624] VALUES (57,'لقد هممت أن أنهى عن الغيلة، فنظرت في الروم وفارس فإذا هم يغيلون أولادهم، فلا يضر أولادهم','48','0','0');
INSERT INTO [t9624] VALUES (58,'أعظم الذنوب عند الله يوم القيامة، أن يلقاه بها بعد الكبائر التي نهى الله عنها، أن يموت','49','0','0');
INSERT INTO [t9624] VALUES (59,'إن كان في شيء شفاء، فشربة عسل، أو شرطة محجم، أو كية بنار، وما أحب أن أكتوي» . لفظهما واحد','50','0','0');
INSERT INTO [t9624] VALUES (60,'صاحب الدابة أحق بصدرها» ، فقال حبيب: إني لا أجهل ما قاله رسول الله صلى الله عليه وسلم،','51','0','0');
INSERT INTO [t9624] VALUES (61,'من أتت عليه ستون سنة، فقد أعذر الله إليه في العمر» ، رواه الإمام أحمد، عن','52','0','0');
INSERT INTO [t9624] VALUES (62,'يسبح من الليل، يعني يصلي، وعائشة رضي الله عنها معترضة بينه وبين القبلة» ، رواه الإمام أحمد،','53','0','0');
INSERT INTO [t9624] VALUES (63,'من صام رمضان وستا من شوال، فكأنما صام السنة كلها» .','53','0','0');
INSERT INTO [t9624] VALUES (64,'الفار من الطاعون كالفار من الزحف، والصابر فيه كالصابر في الزحف» ، رواه الإمام أحمد، عن أبي','54','0','0');
INSERT INTO [t9624] VALUES (65,'الله عز وجل إذا رضي عن العبد، أثنى عليه سبعة أضعاف من الخير لم يعمله، وإذا سخط على العبد','55','0','0');
INSERT INTO [t9624] VALUES (66,'من قال علي ما لم أقل فليتبوأ مقعده من النار، ومن استشاره أخوه المسلم فأشار عليه بغير رشده','55','0','0');
