INSERT INTO [t96257] VALUES (1,'كتاب الطهارة','1','0','0');
INSERT INTO [t96257] VALUES (2,'في غسل اليد قبل دخولها في الإناء، وتوضئ النساء قبل الرجال، أو بعدهم، من إناء واحد، وذكر التسمية عند الوضوء، ومسح الوجه بالمنديل','14','1','0');
INSERT INTO [t96257] VALUES (3,'في الاستنجاء والاستجمار، واستقبال القبلة للخلاء والوطء، وذكر الارتياد للحاجة، وذكر الوسوسة والاستنكاح','19','1','0');
INSERT INTO [t96257] VALUES (4,'في القصد في الماء في الوضوء، والغسل، ومقداره','26','1','0');
INSERT INTO [t96257] VALUES (5,'في صفة الوضوء، وترتيبه، والغسل في أعضائه، والعدد فيه، والتبدئة فيه، والتفرقة في العمد والسهو، وذكر تخليل الأصابع واللحية','28','1','0');
INSERT INTO [t96257] VALUES (6,'في مسح الرأس، والأذنين، وفي المضمضة، والاستنشاق، وفي من نسى بعض مسنون الوضوء، أو مفروضه، أو بعض غسله','35','1','0');
INSERT INTO [t96257] VALUES (7,'في النية في الوضوء والغسل، ومن توضأ لغير الفريضة، أو تطهر كذلك','43','1','0');
INSERT INTO [t96257] VALUES (8,'ذكر ما يوجب الوضوء من الأحداث، ومن شك في الحدث، وذكر المني والودي','46','1','0');
INSERT INTO [t96257] VALUES (9,'ما يوجب الوضوء من: الملامسة، والمباشرة، والقبلة، ومس الفرج، وفي من صلى بعد مس الذكر، أو بعد القبلة ولم يتوضأ','49','1','0');
INSERT INTO [t96257] VALUES (10,'في الوضوء من ما مست النار، ومن ارتد ثم تاب هل يتوضأ؟','54','1','0');
INSERT INTO [t96257] VALUES (11,'في وضوء الجنب والحائض عند النوم','54','1','0');
INSERT INTO [t96257] VALUES (12,'ما يجب من الوضوء على المستحاضة، وسلس البول، والمذي','56','1','0');
INSERT INTO [t96257] VALUES (13,'ذكر ما يوجب الغسل','57','1','0');
INSERT INTO [t96257] VALUES (14,'في صفة الغسل','60','1','0');
INSERT INTO [t96257] VALUES (15,'في من رأى في ثوبه احتلاما، أو امرأة رأت دما لا تدري متى كان، وفي من تطهر لمغيب الحشفة، أو الإنزال، ثم خرج منه المني، أو لاعب ثم صلى بوضوء ثم خرج منه المني','63','1','0');
INSERT INTO [t96257] VALUES (16,'في الغسل من الماء الدائم، وفي تناوله الماء في غسله بعد خروجه من حوض الحمام','66','1','0');
INSERT INTO [t96257] VALUES (17,'في الوضوء والغسل بفضل النصراني والجنب، بسؤره، أو بما ولغ فيه الكلاب، والسباع، والدواب، والدجاج، أو بما قد توضئ به','67','1','0');
INSERT INTO [t96257] VALUES (18,'في البئر، أو الماء الراكد، تموت فيه الدابة، أو تحل فيه النجاسة، وفي الماء تتغير رائحته لغير نجاسة','72','1','0');
INSERT INTO [t96257] VALUES (19,'حكم النجاسة في الثوب والجسد، ومن صلى بذلك، وذكر الدم والمني، وأرواث الدواب، وأبوالها، وألبانها، وبول ما يؤكل لحمه، وما يخرج من ما يشرب النجاسة منها، وذكر لبن النساء','80','1','0');
INSERT INTO [t96257] VALUES (20,'في الثوب يشك في نجاسته، وذكر النضح، وذكر ثوب الحائض والنصراني، وفي التنظف مما لبس بنجس، وهل تزال النجاسة بالماء المضاف','86','1','0');
INSERT INTO [t96257] VALUES (21,'في الماء المشكوك فيه، وفي الماءين، والثوبين أحدهما نجس','89','1','0');
INSERT INTO [t96257] VALUES (22,'في المسح على الخفين','91','1','0');
INSERT INTO [t96257] VALUES (23,'في المسح على الجبائر، أو على الحناء، وشبه ذلك، وفي من حلق رأسه بعد أن مسحه','98','1','0');
INSERT INTO [t96257] VALUES (24,'في التيمم، على صفته، وذكر ما يتيمم عليه، وذكر النية فيه، وفي من لم يجد ماء ولا ترابا','101','1','0');
INSERT INTO [t96257] VALUES (25,'في من له التيمم لعدم الماء، أو المريض، أو غيره، ومتى يتيمم','107','1','0');
INSERT INTO [t96257] VALUES (26,'في من تيمم لصلاة فصلى به غيرها، من نافلة، أو فريضة، وكيف إن كان تيممه لنافلة، وفي التيمم لنافلة، أو لمس مصحف','115','1','0');
INSERT INTO [t96257] VALUES (27,'في الماء بين نفر لا يكفي إلا أحدهم','118','1','0');
INSERT INTO [t96257] VALUES (28,'في وطء المسافر أهله، والجريح، وشبهه','119','1','0');
INSERT INTO [t96257] VALUES (29,'في مس المصحف، وقراءة القرآن، ودخول المسجد للجنب والحائض، أو لغير متوضئ، يمس ما فيه ذكر الله','120','1','0');
INSERT INTO [t96257] VALUES (30,'في الحيض، والطهر، ومبلغ القرء والحيضة','123','1','0');
INSERT INTO [t96257] VALUES (31,'في وطء الحائض، والنفساء، وفي غسل ثوبها، وهل تتوضأ للنوم','128','1','0');
INSERT INTO [t96257] VALUES (32,'جامع القول في المستحاضة','129','1','0');
INSERT INTO [t96257] VALUES (33,'في الحامل ترى الدم على حملها','134','1','0');
INSERT INTO [t96257] VALUES (34,'القول في النفساء','136','1','0');
INSERT INTO [t96257] VALUES (35,'في الوضوء في الصفر، وبالماء الساخن، وغسل اليد من الغمر وغسلها من الطعام وقبله','137','1','0');
INSERT INTO [t96257] VALUES (36,'في زيت الفأرة، وفي البان تطبخ بمائها، وما ينتفع به من الميتة','139','1','0');
INSERT INTO [t96257] VALUES (37,'[كتاب الصلاة]','142','0','0');
INSERT INTO [t96257] VALUES (38,'في فرض الصلاة، وذكر أسمائها، والحكم في من تركها، أو ترك شيئا من أحوالها، وذكر النوافل والمسنون منها','142','37','0');
INSERT INTO [t96257] VALUES (39,'ذكر أوقات الصلوات','149','37','0');
INSERT INTO [t96257] VALUES (40,'في الأذان، والإقامة، ومن يلزمه ذلك وفي من تركه، ووقت الآذان وهل في النوافل أذان، وقيام الناس في الصلاة بعد الإقامة والكلام حينئذ','155','37','0');
INSERT INTO [t96257] VALUES (41,'في هيئة الأذان، والتطريب فيه، والدوران، والركوع بأثره، واستقبال القبلة فيه، والأذان في داخل المسجد، وعلى المنار، وذكر التثويب','158','37','0');
INSERT INTO [t96257] VALUES (42,'في عدد المؤذنين، ومن يؤذن لطائفتين، ومكان المؤذن، والدعاء حينئذ','162','37','0');
INSERT INTO [t96257] VALUES (43,'في أذان الجنب، والمحدث، والصبي، والعبد، وذي الزمانة، والأعمى، ومن لا يرضى، وأذان الراكب والمؤتزر','164','37','0');
INSERT INTO [t96257] VALUES (44,'في السهو في الأذان، والكلام فيه والرعاف، والإغماء، ونحو ذلك','165','37','0');
INSERT INTO [t96257] VALUES (45,'في الإحرام، ورفع اليدين، والتوجه','167','37','0');
INSERT INTO [t96257] VALUES (46,'في قراءة بسم الله الرحمن الرحيم في الفرائض والنوافل، وذكر التعدد في القراءة','169','37','0');
INSERT INTO [t96257] VALUES (47,'في القراءة في الصلاة، وترتيبها وصفتها للإمام والمأموم، والسهو في ذلك، والجهر في النوافل، وتكرير السورة فيها','170','37','0');
INSERT INTO [t96257] VALUES (48,'في صلاة من لا يقرأ، وفي من قرأ بغير القرآن، وفي الإمام ينحصر عن القراءة أصلا أو يدعها في الآخرتين','174','37','0');
INSERT INTO [t96257] VALUES (49,'في القراءة خلف الإمام، وذكر التلقين، وفي تعاي الإمام، وذكر التأمين','175','37','0');
INSERT INTO [t96257] VALUES (50,'جامع العمل في الصلاة؛ من قيام، وقعود، وركوع، وسجود، والنهوض، والتكبير، والاعتماد، ووضع اليد على اليد','179','37','0');
INSERT INTO [t96257] VALUES (51,'في التشهد، والإشارة بالإصبع، والسلام، وذكر الدعاء في تشهده','184','37','0');
INSERT INTO [t96257] VALUES (52,'في القنوت، وذكر الدعاء في الصلاة','188','37','0');
INSERT INTO [t96257] VALUES (53,'في سترة المصلي، والمرور بين يديه، وسترة الإمام، والصلاة بين يديه بصلاته','191','37','0');
INSERT INTO [t96257] VALUES (54,'في استقبال القبلة، وفي من صلى إلى غيرها، وذكر الدليل عليها','194','37','0');
INSERT INTO [t96257] VALUES (55,'في لباس الرجل في الصلاة، والارتداء، وصلاة العريان، والمكفت، والمتشمر، والمتزرر، والصلاة في السراويل، والمؤتزر، ومن عليه آلة الحرب','196','37','0');
INSERT INTO [t96257] VALUES (56,'في اشتمال الصماء في الصلاة، والسدل فيها، وإلقاء الرداء وهو فيها وذكر الصلاة في البرانس والخمائص، والصلاة في النعال','200','37','0');
INSERT INTO [t96257] VALUES (57,'في لباس المرأة الحرة، والأمة، في الصلاة','202','37','0');
INSERT INTO [t96257] VALUES (58,'باب في الأمة تعتق في الصلاة، وصفة خمار الحرة، أو ثوب الرجل يسقط عن عورته في الصلاة','204','37','0');
INSERT INTO [t96257] VALUES (59,'في ذكر النجاسة فيما يصلى به أو عليه، يذكر الدم والميتة والكيمخت، ومن رأى في ثوبه أو ثوب إمامه نجاسة، ومن كان بين يديه في الصلاة نجاسة، أو من لا يتحفظ منها','206','37','0');
INSERT INTO [t96257] VALUES (60,'في من لم يجد إلا ثوبا نجسا أو حريرا، وفي إعادة الصلاة في من صلى بذلك، ووقت من يعيد في ذلك، وذكر صلاة المعطوبين لا يجدون ثيابا','212','37','0');
INSERT INTO [t96257] VALUES (61,'ما يكره أن يصلى فيه من الأماكن، أو يصلى عليه مما يشك فيه','216','37','0');
INSERT INTO [t96257] VALUES (62,'باب في الصلاة على البسط والثياب، أو إلى ما فيه تماثيل، وفي حمل الحصباء من الشمس إلى الظل','221','37','0');
INSERT INTO [t96257] VALUES (63,'باب ما يكره من لباس الحرير والذهب في الصلاة وغيرها','222','37','0');
INSERT INTO [t96257] VALUES (64,'في إلإقبال على الصلاة، والخشوع فيها، والبكاء، والمشى إليها، وذكر الله فيها جوابا أو استرجاعا أو قعودا، أو نحو هذا، والنظر إلى الشىء فيها','226','37','0');
INSERT INTO [t96257] VALUES (65,'في التسبيح للحاجة، أو للإمام في الصلاة، وذكر الإشارة، والتنحنح، والنفخ، والعطاس والتثاؤب','229','37','0');
INSERT INTO [t96257] VALUES (66,'ذكر ما يستخف من العمل في الصلاة، وفي المصلى يحمل شيئا، أو يقتل عقربا، أو يخاف على صبي أو من شىء فوتا، وهل يلقي رداءه في الصلاة','232','37','0');
INSERT INTO [t96257] VALUES (67,'باب في الكلام، والضحك، والتبسم في الصلاة','236','37','0');
INSERT INTO [t96257] VALUES (68,'في من صلى وبه حقن أو غثيان، وهل يصلي عند حضور الطعام','237','37','0');
INSERT INTO [t96257] VALUES (69,'في الرعاف في الصلاة، وما يبنى منه، كيف البناء فيه، وفي من لا يكف عنه الدم كيف يصلي','238','37','0');
INSERT INTO [t96257] VALUES (70,'في من رعف في صلاة الجنازة، أو العيدين، أو رأى في ثوبه نجاسة','244','37','0');
INSERT INTO [t96257] VALUES (71,'ذكر ما يعرض في الصلاة من القىء، والحدث، وسيلان الدم، من مالا يبنى فيه، ومن كان منه ما يقطع الصلاة بعد التشهد','244','37','0');
INSERT INTO [t96257] VALUES (72,'في الصلاة على الدابة لمرض أو خوف، والتنفل عليها، وفي الصلاة على السرير، وهل يتنفل الراكب أو الماشي، وهل يصلي الخائف وهو جالس أو ماش','246','37','0');
INSERT INTO [t96257] VALUES (73,'في صلاة أهل السفينة، وهل يتنفل فيها إلى غير القبلة، وصلاة المائد فيها، وفي صلاة المعطوبين وهم في البحر، أو خارجين منه عراة، وفي من ربطه اللصوص، ومن وقع عليه الهدم','248','37','0');
INSERT INTO [t96257] VALUES (74,'في صلاة الرجل في الماء والطين','250','37','0');
INSERT INTO [t96257] VALUES (75,'في صلاة المريض، والزمن، والقادح، والضعيف، وفي الأعمى يسجد قبل إمامه ولا يعلم','251','37','0');
INSERT INTO [t96257] VALUES (76,'في صلاة الجالس، وتنفله، وفي إمامة الجالس في المكتوبة والنافلة','256','37','0');
INSERT INTO [t96257] VALUES (77,'باب في جمع المريض بين الصلاتين','258','37','0');
INSERT INTO [t96257] VALUES (78,'في جمع المسافر بين الصلاتين، والجمع بعرفة، وهل يجمع الحاضر بينهما','260','37','0');
INSERT INTO [t96257] VALUES (79,'في الجمع ليلة المطر','262','37','0');
INSERT INTO [t96257] VALUES (80,'في صلاة الصبيان، وصيامهم، وتفريقهم في المضاجع، وصلاة الأعجمي من المجوس، وغسل من أسلم وصلاته','265','37','0');
INSERT INTO [t96257] VALUES (81,'باب في مقادير الوقت، والنصراني يسلم، والمغمى عليه يفيق، والمرأة تحيض أو تطهر، والمسافر يظعن أو يقدم، كيف إن ذكر صلاة، هل هي أملك بالوقت','268','37','0');
INSERT INTO [t96257] VALUES (82,'في تقدير الوقت للحائض تطهر، ولمن أسلم أو أفاق من الإغماء، هل هو بعد الفراغ من الغسل أو الوضوء للمفيق، أو قبل، أو كان ثوب أحدهم نجسا، كيف إن قدروا فأخطأوا التقدير، أو تبينوا نجاسة الماء، أو انتقض وضوء المتوضئ','271','37','0');
INSERT INTO [t96257] VALUES (83,'في من قدم أو ظعن وعليه صلاتا يومه، أو إحداهما، وكيف إن ذكر صلاة فائتة، أو صلى بثوب نجس ما فات، أو لم يفت، والوقت في ذلك، وفيما يعيد بعد قضاء الفائتة، وكيف إن ذكر سجدة','275','37','0');
INSERT INTO [t96257] VALUES (84,'في الإمامة، ومن هو أحق بها','277','37','0');
INSERT INTO [t96257] VALUES (85,'في من لا يجوز أن يؤم، ومن يكره إمامته','280','37','0');
INSERT INTO [t96257] VALUES (86,'في الصلاة خلف أهل البدع، ومن لا يرضى حاله من الولاة، وفي من ائتم بنصراني، ولم يعلم','285','37','0');
INSERT INTO [t96257] VALUES (87,'وجه العمل في الإمامة للإمام والمأموم','287','37','0');
INSERT INTO [t96257] VALUES (88,'في اتصال الصفوف، وسد الفرج، وذكر الصف الأول، وذكر صفوف النساء، كيف إن صلين بين الرجال، وفي الصلاة بين يدي الإمام، وصلاته أرفع من أصحابه','290','37','0');
INSERT INTO [t96257] VALUES (89,'في اتباع الإمام، والعمل قبله','295','37','0');
INSERT INTO [t96257] VALUES (90,'في من أتى والإمام راكع، وهل يحرم قبل أن يصل إلى الصف، كيف إن ركع بعد رفع الإمام','297','37','0');
INSERT INTO [t96257] VALUES (91,'في من ضغط أو نعس أو غفل عن الركوع حتى رفع الإمام أو سجد، أو غفل عن السجود، أو ذكر سجدة بعد سلام إمامه في الجمعة وغيرها','299','37','0');
INSERT INTO [t96257] VALUES (92,'في اختلاف نية الإمام والمأموم في صلاتين مختلفتين، أو حضرية وسفرية','303','37','0');
INSERT INTO [t96257] VALUES (93,'في الإمام تفسد صلاته، أو يذكر جنابة أو صلاة، أو يفعل ما يبطلها، أو يستخلف ثم يرجع فيخرج المستخلف، أو ينتظرونه لا يستخلف','306','37','0');
INSERT INTO [t96257] VALUES (94,'في الإمام يسلم من ركعتين، فيسبح به، فيبتدئ الصلاة فيتبع، وفي المستخلف يبتدئ الصلاة','310','37','0');
INSERT INTO [t96257] VALUES (95,'في استخلاف الإمام، كيف يعمل المستخلف، وكيف إن لم يستخلف فصلوا وحدانا، أو بإمامين، أو لم يكن خلفه إلا واحد','312','37','0');
INSERT INTO [t96257] VALUES (96,'في الإمام يقدم من فاتته ركعة أو بقى منها السجود، أو لم يدر ما فاته، أو أحرم بعد أن قدمه، والمقيم يقدمه المسافر وقد فاتته ركعة، والقوم تفوتهم الركعة، هل يصلونها بإمام؟','314','37','0');
INSERT INTO [t96257] VALUES (97,'في قضاء المأموم، والعمل فيما يدرك ويقضي، وهل يأتم به فيما يقضي من فاته ذلك أو غيره','317','37','0');
INSERT INTO [t96257] VALUES (98,'في الرجلين يؤم أحدهما الآخر، ثم يشكان في الإمام في التشهد الآخر، أو قبله، كيف إن كان أحدهما مسافرا، وفي الإمام يرجع مأموما','320','37','0');
INSERT INTO [t96257] VALUES (99,'في إعادة الصلاة في جماعة، كيف إن بطلت إحداهما، أو ذكر أنه لم يكن صلاها، أو ذكر من أحدهما سجدة أو أنه صلاها على غير وضوء','321','37','0');
INSERT INTO [t96257] VALUES (100,'في من أقيمت عليه الصلاة، وهو في صلاة','326','37','0');
INSERT INTO [t96257] VALUES (101,'في الجمع في المسجد مرتين','327','37','0');
INSERT INTO [t96257] VALUES (102,'في من دخل من صلاة إلى صلاة، في نافلة أو فريضة، سهوا، أو ذكر في التي دخل فيها شيئا بقى عليه من الأخرى، أو سجدتي السهو','328','37','0');
INSERT INTO [t96257] VALUES (103,'في من ذكر صلاة أو صلوات، في وقت صلاة، أو عند طلوع الشمس، أو عند غروبها','331','37','0');
INSERT INTO [t96257] VALUES (104,'في من ذكر صلاة في صلاة، أو بعد أن سلم منها','334','37','0');
INSERT INTO [t96257] VALUES (105,'في المصلي يتمادى على شك وضوء أو نجاسة، أو يسلم على شك من التمام فيذكر، أو حالت نيته إلى نافلة، أو صلاة أخرى، ثم ذكر، أو زاد ركعة سهوا، ثم ذكر سجدة أو أم القرآن، أو زادها عمدا، ثم ذكر أنها عليه','337','37','0');
INSERT INTO [t96257] VALUES (106,'في السهو عن تكبيرة الإحرام، أو الشك فيها للإمام والمأموم، وكيف إن كبر للركوع، وذكر ما يحمله الإمام وما لا يحمله عن المأموم، من السهو وغيره','341','37','0');
INSERT INTO [t96257] VALUES (107,'في السهو عن القراءة','346','37','0');
INSERT INTO [t96257] VALUES (108,'في السهو عن الإجهار والإسرار في القراءة، وكيف إن رجع هل يسجد، وكيف إن فعل ذلك عامدا','351','37','0');
INSERT INTO [t96257] VALUES (109,'في السهو عن تكبير الخفض والرفع وشبهه، والسهو عن تمام الركوع والسجود، وعن التشهد والسلام','352','37','0');
INSERT INTO [t96257] VALUES (110,'جامع القول في السهو، وفي من زاد أكثر من ركعة، وفي من رجع لإصلاح ما بقى عليه بعد أن فارق صلاته','355','37','0');
INSERT INTO [t96257] VALUES (111,'في من يكثر شكه وسهوه أو يستنكحه ذلك، أو عليه سهون، أو يلزمه سجود بعد السلام فيسجد قبل، أو يلزمه سجود قبل فيسجد بعد','359','37','0');
INSERT INTO [t96257] VALUES (112,'في العمل في سجدتي السهو، وذكر السهو فيها','361','37','0');
INSERT INTO [t96257] VALUES (113,'في من ذكر سجدتي السهو بعد انصرافه، أو في صلاة أخرى','363','37','0');
INSERT INTO [t96257] VALUES (114,'في السهو في الوتر، وركعتي الفجر، والنوافل','365','37','0');
INSERT INTO [t96257] VALUES (115,'في من ذكر سجدة أو ركعة بعد أن سلم، وهو وحده، أو خلف إمام وجد ما يبنى فيه إذا انصرف','368','37','0');
INSERT INTO [t96257] VALUES (116,'في من ذكر سجدة فأكثر، أو الركوع، وهو في آخر صلاته، أو قبل آخرها، أو شك في ذلك، وكيف إن كان ذلك مع إمام','369','37','0');
INSERT INTO [t96257] VALUES (117,'في من ذكر سجدة، وهو مأموم','374','37','0');
INSERT INTO [t96257] VALUES (118,'في الإمام يذكر سجدة، أو ركعة، أو يشك فيها ومن خلفه في يقين أو شك وقد سجدوها دونه، وهل يتبعه من فاتته ركعة فيما يأتي به','377','37','0');
INSERT INTO [t96257] VALUES (119,'في الإمام يدع سجدة، فيسبح به، فلا يرجع أو يترك سجود السهو، وفي رجوع الإمام في شكه إلى يقين من خلفه، ورجوعهم إلى يقينه في شكهم، وهل يقبل قول من ليس معهم في صلاة','382','37','0');
INSERT INTO [t96257] VALUES (120,'في الإمام يصلي خامسة، فيتبعه بعض من معه، وكيف إن قال بعد أن سلم: ذكرت سجدة','384','37','0');
INSERT INTO [t96257] VALUES (121,'في سهو المأموم مع الإمام، أو فيما يقضي، كيف إن ظن أنه سلم، فقام للقضاء، أو انصرف، يذكر ما يحمله الإمام','386','37','0');
INSERT INTO [t96257] VALUES (122,'في الذى يفوته بعض صلاة إمامه ويذكر سجدة قبل يقضي أو بعد وفي الإمام أو المستخلف يذكر سجدة، أو يذكر ذلك الإمام لمن استخلفه','388','37','0');
INSERT INTO [t96257] VALUES (123,'في من فاته بعض الصلاة فقضاه، أو استخلف عليه فصلاه، ثم ذكر الأول سجدة','390','37','0');
INSERT INTO [t96257] VALUES (124,'في من أدرك ركعة من الجمعة، ثم ذكر بعد القضاء أو قبله سجدة، أو بعد أن صلاها مستخلفا، أو ذكرها الإمام الأول','396','37','0');
INSERT INTO [t96257] VALUES (125,'في الإمام في صلاة الخوف يذكر سجدة','400','37','0');
INSERT INTO [t96257] VALUES (126,'في من فاته بعض الصلاة وعلى الإمام سهو، وكيف إن سها فيما يقضي، أو فيما استخلف عليه','402','37','0');
INSERT INTO [t96257] VALUES (127,'في من ذكر سجدة أو سجدتين من صلوات','406','37','0');
INSERT INTO [t96257] VALUES (128,'في من ذكر صلاة لا يدري ما هي، أو صلوات لا يدري أيتهن قبل صاحبتها، كيف إن لم يدر أسفر أم حضر','407','37','0');
INSERT INTO [t96257] VALUES (129,'في إقصار الصلاة للمسافر، ومتى يقصر في خروجه ورجوعه، كيف إن رجع لحاجة','415','37','0');
INSERT INTO [t96257] VALUES (130,'في أقل ما يقصر فيه المسافر من السفر، كيف إن نوى الإقامة بين أضعاف سفره، أو يقيم لأمر لا يعلم غايته، في خلال سفره أو في ابتدائه','418','37','0');
INSERT INTO [t96257] VALUES (131,'في صلاة المكي والمنوي في مسيرهم إلى عرفة، وفي رجوعهم إلى منى، وإلى مكة، وصلاة العرفي إذا أفاض، ومن كان أقام بمكة قبل أن يحج من أهل الآفاق','422','37','0');
INSERT INTO [t96257] VALUES (132,'في المسافر يمر لقرية فيها أهله، أو يحدث فيها أهلا، أو ينوى الإقامة بموضع، وهو به، أو إليه خارج أو رجع إليه','423','37','0');
INSERT INTO [t96257] VALUES (133,'في المسافر ينوى الإقامة، وكيف إن نوى ذلك في صلاته وهو إمام أو فذ، كيف إن عاد إلى نية السفر، وكيف إن عاد إلى وطنه','426','37','0');
INSERT INTO [t96257] VALUES (134,'في المسافر يتم الصلاة، وفي ائتمامه بمقيم، وائتمام المقيم به، وفي إمام مسافر يتم الصلاة بمن خلفه','428','37','0');
INSERT INTO [t96257] VALUES (135,'في السفري يؤم الحضريين كيف يبنون بعده، وفي الحضري يدرك من صلاة المسافر ركعة، كيف يقضي ويبنى، كيف إن استخلفه السفري','432','37','0');
INSERT INTO [t96257] VALUES (136,'في إمام مسافر صلى ركعة، ثم أحدث، فقدم حضريا، وهل يؤتم به فيما يبنى، كيف إن جهلوا بالأول أحضري أم سفري','434','37','0');
INSERT INTO [t96257] VALUES (137,'في الإمام الحضري يقدم مسافرا، وكيف إن قال له: ذكرت سجدة مما صليت، وكيف إن قال ذلك بعد قضاء السفريين','436','37','0');
INSERT INTO [t96257] VALUES (138,'في المسافر يصلي ركعة، فيذهب الوقت، ثم ينوى الإقامة، أو يغمى عليه، أو تحيض المرأة حينئذ، وقد خرج وقت تلك الصلاة بعد الركعة','439','37','0');
INSERT INTO [t96257] VALUES (139,'في من أحرم بصلاة حضر، فذكر فيها أنها عليه سفرية، أو ذكر أن عليه ثوبا نجسا، أو حالت نيته بعد أن أحرم على ما لزمه','440','37','0');
INSERT INTO [t96257] VALUES (140,'في من خرج لمقدار من الوقت، ناسيا لسجدة أو سجدتين من الظهر والعصر، وكيف إن صلى الصلاتين سفريتين، ثم ذكر سجدة من أحدهما بعد ما سلم، أو قبل','441','37','0');
INSERT INTO [t96257] VALUES (141,'في من سافر، أو قدم لوقت، أو امرأة تحيض، أو تطهر، وعليهم صلاة أو صلوات، وكيف إن لم يدر المسافر أمن يوم قدومه أو من يوم خروجه','443','37','0');
INSERT INTO [t96257] VALUES (142,'في إلزام الجمعة، ومن يلزمه السعي، وصفة القرى التى يجمع أهلها، وهل يجمع في المصر في موضعين؟','447','37','0');
INSERT INTO [t96257] VALUES (143,'في تخلف الإمام عن الجمعة، أو هروب الناس عنه','450','37','0');
INSERT INTO [t96257] VALUES (144,'في من يعذر بالتخلف عن الجمعة، ومن لا يعذر، وهل لمن حضر العيد في يومها أن يتخلف عنها','452','37','0');
INSERT INTO [t96257] VALUES (145,'في الرجل أيسافر يوم الجمعة قبل أن يصليها، والمسافر هل يأتيها؟ وكيف إن صلى الظهر ثم دخل العصر، هل يصليها؟','454','37','0');
INSERT INTO [t96257] VALUES (146,'في من فاتته الجمعة، هل يصلي في جماعة؟','456','37','0');
INSERT INTO [t96257] VALUES (147,'في من صلى الظهر قبل الإمام يوم الجمعة، أو صلاها من لا تجب عليه ثم صلى الجمعة، وفي الإمام يصلي بالناس ظهرا في وقت الجمعة، ومن لم يدر أجمع إمامه أم صلى ظهرا','457','37','0');
INSERT INTO [t96257] VALUES (148,'في الغسل للجمعة، والتهجير إليها، والتطيب والزينة لها','459','37','0');
INSERT INTO [t96257] VALUES (149,'في وقت الجمعة والنداء إليها','462','37','0');
INSERT INTO [t96257] VALUES (150,'في البيع وغيره بعد النداء يوم الجمعة','464','37','0');
INSERT INTO [t96257] VALUES (151,'في الخطبة يوم الجمعة، والعمل فيها، والتنفل قبلها، والتخطي','465','37','0');
INSERT INTO [t96257] VALUES (152,'في الإنصات للخطبة، وما للناس فعله حينئذ والإمام','470','37','0');
INSERT INTO [t96257] VALUES (153,'في من خطب على غير وضوء، أو أحدث، أو خطب قبل الزوال، أو خطب ثم أخر الصلاة، أو عرض له أمر ثم ضعف، وهل يحتبي في الخطبة، وغير ذلك من مسائل الخطبة','472','37','0');
INSERT INTO [t96257] VALUES (154,'في الإمام في الجمعة، والقراءة فيها، والقنوت، وإمامة العبد فيها والمسافر والإمام الجائر، وهل تصلى في الأفنية','473','37','0');
INSERT INTO [t96257] VALUES (155,'في الاستخلاف في صلاة الجمعة، أو في الخطبة، وهل يصلي من لم يشهد الخطبة، وكيف إن ذكر صلاة نسيها، وفي الإمام يعزل ويأتي غيره بعد الخطبة','475','37','0');
INSERT INTO [t96257] VALUES (156,'جامع القول في صلاة الخوف من العدو أو من لصوص أو سباع','478','37','0');
INSERT INTO [t96257] VALUES (157,'في ترتيب صلاة الخوف في المغرب، ومن فاته منها ركعة، وكيف إن صلى بكل طائفة ركعة فيها، أو في غيرها في الحضر، وفي الخوف ينكشف في بعض الصلاة، وفي الاستخلاف في صلاة الخوف','482','37','0');
INSERT INTO [t96257] VALUES (158,'في جمع الصلاتين بمزدلفة وعرفة','484','37','0');
INSERT INTO [t96257] VALUES (159,'القول في صلاة الوتر','485','37','0');
INSERT INTO [t96257] VALUES (160,'في الوتر بعد الفجر، ومن ذكرها في الصبح','488','37','0');
INSERT INTO [t96257] VALUES (161,'ما جاء في ركعتي الفجر','489','37','0');
INSERT INTO [t96257] VALUES (162,'في صلاة العيدين، وعلى من هي، وأين تصلى، والخروج إليها، والغسل لها، والتكبير في ذلك','493','37','0');
INSERT INTO [t96257] VALUES (163,'صفة صلاة العيدين، وذكر السهو فيها، والحدث والرعاف، ومن أدرك بعضها، والتنفل قبلها وبعدها','497','37','0');
INSERT INTO [t96257] VALUES (164,'ذكر الخطبة في العيدين وسنتها','500','37','0');
INSERT INTO [t96257] VALUES (165,'في التكبير أيام التشريق دبر الصلوات، وهل يكبر دبر الصلوات في غيرها بأرض العدو أو غيرها','502','37','0');
INSERT INTO [t96257] VALUES (166,'في التحية بالدعاء في العيدين','505','37','0');
INSERT INTO [t96257] VALUES (167,'ما جاء في صلاة الخسوف','505','37','0');
INSERT INTO [t96257] VALUES (168,'ما جاء في صلاة الاستسقاء','508','37','0');
INSERT INTO [t96257] VALUES (169,'في قيام رمضان، في صلاة الليل، وذكر الاستعاذة، وقنوت الوتر','517','37','0');
INSERT INTO [t96257] VALUES (170,'جامع القول في صلاة النوافل','521','37','0');
INSERT INTO [t96257] VALUES (171,'في الاجتماع للقراءة بألحان، أو بغير ألحان، أو للتعليم','525','37','0');
INSERT INTO [t96257] VALUES (172,'في الدعاء ورفع اليدين','526','37','0');
INSERT INTO [t96257] VALUES (173,'باب في مس المصحف، وذكر حليته وشكله، وشىء من ذكر القراءة، وذكر ما يعلق من القرآن يستشفى به','527','37','0');
INSERT INTO [t96257] VALUES (174,'باب جامع في المساجد، وفيه شىء من ذكر الشعر','528','37','0');
INSERT INTO [t96257] VALUES (175,'باب جامع','534','37','0');
INSERT INTO [t96257] VALUES (176,'كتاب الجنائز','537','0','0');
INSERT INTO [t96257] VALUES (177,'في توجيه الميت، وتلقينه، وإغماضة إذا قضى','537','176','0');
INSERT INTO [t96257] VALUES (178,'في غسل الميت، وستر عورته، وهل يحلق له شعر أو يقص له ظفر؟','538','176','0');
INSERT INTO [t96257] VALUES (179,'في الميت، هل ينجس الثوب الذى ينشف به، وما يصيبك من مائه، وهل على غاسله غسل، أو على حامله وضوء، وهل تغسله الحائض والجنب؟','542','176','0');
INSERT INTO [t96257] VALUES (180,'في غسل من جدر أو شدخ وشبهه، وغسل بعض الجسد، أو الميت ينبش، ومن غسل هل يؤخر تكفينه أو حمله؟ وفي الموتى يكثرون، والعمل في غسلهم ودفنهم','543','176','0');
INSERT INTO [t96257] VALUES (181,'في جنب وميت معهما ما يكفي أحدهما','544','176','0');
INSERT INTO [t96257] VALUES (182,'في غسل أحد الزوجين صاحبه، والسيد يغسله من فيه بقية رق من إمائه','545','176','0');
INSERT INTO [t96257] VALUES (183,'في غسل ذوي المحارم بعضهم بعضا، والمرأة تموت لا نساء معها، والرجل يموت لا رجال معه، غسل النساء الصبي، والرجال الصبية','547','176','0');
INSERT INTO [t96257] VALUES (184,'في تكفين الميت، وتحنيطه','550','176','0');
INSERT INTO [t96257] VALUES (185,'في صفة كفن الميت، وعدد أثوابه، والقصد فيه، والوصية به، وكفن المديان، والميت ينبش، والكفن يتلف','552','176','0');
INSERT INTO [t96257] VALUES (186,'في التكفين في الحرير، والخز، والمصبغ','559','176','0');
INSERT INTO [t96257] VALUES (187,'في من يلزم الرجل أن يكفنه ويقبره','560','176','0');
INSERT INTO [t96257] VALUES (188,'في تقبيل الميت، وتعجيل دفنه، والإيذان بجنازته','562','176','0');
INSERT INTO [t96257] VALUES (189,'في هيئة النعش وفرشه، وتغطية الميت بثوب، والتقبب على نعش المرأة','564','176','0');
INSERT INTO [t96257] VALUES (190,'وجه العمل في حمل الميت، والمسير به، واتباعه، والمشى أمامه، والتزاحم على نعشه، والترجل، والتحسر، وهل يتبع بنار، أو يرفع الصوت بالدعاء له، أو يجلس قبل أن يوضع أو ينصرف قبل أن يقبر','565','176','0');
INSERT INTO [t96257] VALUES (191,'في حمل الميت من بلد إلى بلد قبل أن يدفن أو بعد، وفي تحوله بعد أن دفن من قبر إلى قبر','569','176','0');
INSERT INTO [t96257] VALUES (192,'في البكاء على الميت والنياحة، وخروج النساء، وفي صلاتهن، وفي الطعام يصنع لأهل الميت','570','176','0');
INSERT INTO [t96257] VALUES (193,'في شهود الجنائز وفضلها، وهل يقام للجنازة إذا أقبلت','575','176','0');
INSERT INTO [t96257] VALUES (194,'في الاستكانة في الجنازة، وكراهة الضحك فيها','577','176','0');
INSERT INTO [t96257] VALUES (195,'في من هو أحق بالصلاة على الميت، من أوليائه، وكيف إن قدم أقربهم أجنبيا، أو أوصى به الميت، ومن أولى بإنزال المرأة في قبرها','578','176','0');
INSERT INTO [t96257] VALUES (196,'في الجنازة يحضرها الأمير، والقاضي، وإمام الصلاة، أو من له الفضل، مع أوليائها، وفي الجنازتين تحضران، لكل واحدة ولي، من أحق بالصلاة في ذلك كله','580','176','0');
INSERT INTO [t96257] VALUES (197,'ما يجب من الصلاة على الجنازة، وعدد التكبير عليها، وأين يقف الامام منها، ورفع الأيدي فيها، والسلام منها، وإمامة المرأة فيها','583','176','0');
INSERT INTO [t96257] VALUES (198,'ذكر الدعاء للميت في الصلاة عليه، وترك القراءة، وهل يدعى بعد الرابعة، وما يدعى به للطفل','587','176','0');
INSERT INTO [t96257] VALUES (199,'في الصلاة على الصغير، والمنفوس المستهل، وغسله، وهل يصلي على من لم يستهل، وعلى السقط، وفي المرأة الكافرة تموت وهي حامل من مسلم','592','176','0');
INSERT INTO [t96257] VALUES (200,'في النفساء تموت وقد استهل منفوسها، أو لم يستهل، هل يحمل معها، أو يجمعان في صلاة؟','594','176','0');
INSERT INTO [t96257] VALUES (201,'في حكم الصغير من السبي يسلم، أو يسلم أحد أبويه، أو ينوي مبتاعه إدخاله في الإسلام في الصلاة عليه إن مات، وفي الموارثة، والقود، وغيره، وإسلام الكبير الأعجمي عن تعليم','594','176','0');
INSERT INTO [t96257] VALUES (202,'في الصلاة على المرتد الصغير، ومن أسلم في صغره، ثم رجع بعد بلوغه أو قبل، أو أسلم الأب وثبت الولد كافرا','601','176','0');
INSERT INTO [t96257] VALUES (203,'في الصلاة على ولد المسلم، يولد مخبولا، ومن جن بعد البلوغ، والقول في مصير أولاد المسلمين والكافرين','603','176','0');
INSERT INTO [t96257] VALUES (204,'في الصلاة على الصغير المنبوذ، والكبير المجهول، وبالبلد مسلمون ونصارى','604','176','0');
INSERT INTO [t96257] VALUES (205,'في الموتى فيهم كافر لا يعرف، هل يصلى عليهم؟','607','176','0');
INSERT INTO [t96257] VALUES (206,'في الذمي يموت، وليس معه إلا المسلمون، هل يواروه، وفي المسلم يموت أبوه الكافر، هل يلي أمره أو يعزي فيه، أو مات الابن هل يليه أبوه؟','608','176','0');
INSERT INTO [t96257] VALUES (207,'في الصلاة على قتلى الخوارج، وأهل البدع، وأهل المعصية','608','176','0');
INSERT INTO [t96257] VALUES (208,'في الصلاة على من قتل بقود، أو في حد أو قاتل نفسه، والصلاة على أهل الكبائر، وعلى ولد الزنى','609','176','0');
INSERT INTO [t96257] VALUES (209,'جامع القول في الشهيد، والصلاة عليه، وفي غير ذلك من شأنه، وفي من قتل مظلوما، أو قتله لصوص أو خوارج','611','176','0');
INSERT INTO [t96257] VALUES (210,'في الصلاة على بعض الجسد أو على الغائب','615','176','0');
INSERT INTO [t96257] VALUES (211,'في الصلاة على الجنازة في المسجد، أو في المقبرة، أو في الدور','617','176','0');
INSERT INTO [t96257] VALUES (212,'في الصلاة على الجنازة بعد الصبح، وبعد العصر، أو في الليل','620','176','0');
INSERT INTO [t96257] VALUES (213,'في الصلاة على الجنائز، إذا اجتمعت، وكيف توضع','622','176','0');
INSERT INTO [t96257] VALUES (214,'في الجنازتين ينوي الإمام بالصلاة أحدهما، ومن خلفه، ينويهما جمعيا، وفي الجنازة لا يدري من صلى عليها أرجل هي أم امرأة؟','625','176','0');
INSERT INTO [t96257] VALUES (215,'في من دفن ولم يصل عليه، أو لم يغسل، أو نسى عليه بعض التكبير، أو كبر عليه بغير دعاء، أو دفن في مقبرة النصارى','626','176','0');
INSERT INTO [t96257] VALUES (216,'في من صلى عليه إلى غير القبلة، أو جعل في لحده كذلك، أو جعل رأسه موضع رجليه في قبره، أو في الصلاة عليه، أو صلوا عليه جلوسا أو ركوبا','629','176','0');
INSERT INTO [t96257] VALUES (217,'في إمام الجنازة يحدث، أو يذكر جنابة، أو صلاة، أو يضحك، أو يرعف','631','176','0');
INSERT INTO [t96257] VALUES (218,'في من فاته بعض التكبير على الجنازة، وكيف إن كان الإمام يكبر خمسا، ومن لم يعلم ببعض تكبير الإمام حتى سلم','632','176','0');
INSERT INTO [t96257] VALUES (219,'في الجنازة، هل يصلى عليها من خاف فوتها بالتيمم، وهل يصلى عليها قبل الدفن من فاتته؟','634','176','0');
INSERT INTO [t96257] VALUES (220,'في الميت يدفن وقد ابتلع مالا، أو جوهرا، أو المرأة تموت بجمع','635','176','0');
INSERT INTO [t96257] VALUES (221,'في الميت يوارى، وقد نسوا في القبر مالا، أو ثوبا أو غيره','637','176','0');
INSERT INTO [t96257] VALUES (222,'في إنزال الميت في قبره، ومن ينزل المرأة، وفي سترة القبر، وما يدعى للميت عند إقباره','638','176','0');
INSERT INTO [t96257] VALUES (223,'في الميت في البحر، كيف يوارى، وكيف إن ألقاه البحر بعد أن صلي عليه، وألقي فيه','641','176','0');
INSERT INTO [t96257] VALUES (224,'في جمع الميتين في قبر أو كفن','642','176','0');
INSERT INTO [t96257] VALUES (225,'في اللحد والشق، وإكفائه باللبن أو غيرها، والحثيان في القبر، وإعماقه، وتسنيمه، ورشه والدفن في التابوت في القبر، وهل يدفن على الموتى، ومن دفن في قبر لغيره، أو أرضه','643','176','0');
INSERT INTO [t96257] VALUES (226,'في إقبار الميت، والصلاة عليه ليلا','647','176','0');
INSERT INTO [t96257] VALUES (227,'في البناء على القبور، وتجصيصها، والكتاب عليها، وبناء المساجد عليها، والجلوس والمشي وزيارتها','648','176','0');
INSERT INTO [t96257] VALUES (228,'في بقاء الروح، وذكر النفس والروح، وذكر فتنة القبر','653','176','0');
INSERT INTO [t96257] VALUES (229,'في التعزية بمصيبة الموت، وهل يعزى الكافر؟','656','176','0');
INSERT INTO [t96257] VALUES (230,'في حضور المسلم جنازة الكافر، أو حمله، أو القيام على قبره، وحضور الكافر للمسلم','659','176','0');
INSERT INTO [t96257] VALUES (231,'مما جاء في موت الغريب','660','176','0');
INSERT INTO [t96257] VALUES (232,'في ضرب الفسطاط على القبر','661','176','0');
INSERT INTO [t96257] VALUES (233,'في من جمع له ثمن كفن، فكفن في غيره، وفي من ابتاع كفنا لمنازع، على أنه إن لم يمت رده','662','176','0');
INSERT INTO [t96257] VALUES (234,'كتاب الصوم','663','0','0');
INSERT INTO [t96257] VALUES (235,'والاعتكاف في الصوم، والفطر لرؤية الهلال، وذكر صوم يوم الشك، ومن رأى الهلال وحده','663','234','0');
INSERT INTO [t96257] VALUES (236,'ذكر ما يصام به أو يفطر من الشهادة على الهلال، أو الاستفاضة فيه','665','234','0');
INSERT INTO [t96257] VALUES (237,'في الهلال يثبت رؤيته عند أهل بلد هل يلزم غيرهم أن يعملوا على ذلك؟ أو يثبت عند عالم بعيانه، ويكون القاضي ممن لا يعبأ به، هل يلزم من ببلده؟','669','234','0');
INSERT INTO [t96257] VALUES (238,'في رؤية الهلال قبل الزوال أو بعده','670','234','0');
INSERT INTO [t96257] VALUES (239,'في التبييت في الصيام','671','234','0');
INSERT INTO [t96257] VALUES (240,'في تعجيل الفطر، وتأخير السحور، وفي من شك في الفجر، أو في الغروب، وكيف إن أكل بعد شكه، وهل يصدق المؤذن','675','234','0');
INSERT INTO [t96257] VALUES (241,'في الصوم في السفر في رمضان، وغيره','677','234','0');
INSERT INTO [t96257] VALUES (242,'في المسافر يفطر بعد التبييت، أو قبل أن يخرج، أو بعد أن يقدم، وكيف إن قدم مفطرا، أو يفطر بعد أن كسر، وما تعذر له من التأويل في ذلك','680','234','0');
INSERT INTO [t96257] VALUES (243,'في صيام الجنب، والحائض وفي المغمى عليه يفيق، وما يحدث من ذلك في الصوم، أو ينكشف فيه قبل الفجر، أو بعده','683','234','0');
INSERT INTO [t96257] VALUES (244,'في صيام الصغير، والمجنون، والسفيه، والمغمى عليه','686','234','0');
INSERT INTO [t96257] VALUES (245,'في صوم النصراني يسلم وصوم من ملك من رقيق العجم والمجوس','688','234','0');
INSERT INTO [t96257] VALUES (246,'في صيام الأسير، أو غيره ببلد الحرب تحريا، وفي من صام رمضان قضاء عن غيره، من فرض أو واجب','689','234','0');
INSERT INTO [t96257] VALUES (247,'في صوم الشيخ الزمن، والحامل، والمرضع، والمستعطش، وما يجب بإفطارهم','691','234','0');
INSERT INTO [t96257] VALUES (248,'فيما يعذر به الصائم في الفطر، من المرض، أو من رمد، أو عطش، أو شرق، أو غيره ومن أفطر لعذر ثم زال عنه، هل يتمادى مفطرا في يومه؟','692','234','0');
INSERT INTO [t96257] VALUES (249,'في الصائم يفطر متأولا، وما يعذر به من ذلك في رفع الكفارة، وما لا يعذر به','695','234','0');
INSERT INTO [t96257] VALUES (250,'في من أفطر مكرها، أو أدخل حلقه شىء لم يتعمده، أو أمر غالب، من ذباب، أو عود، أو ماء، أو دقيق، أو كبار، أو غيره','696','234','0');
INSERT INTO [t96257] VALUES (251,'في ذوق الطعام للصائم ومضغه، ومداواة الحفر، وابتلاع ما بين أسنانه، وابتلاع الحصاة والنواة ونحوها، وازدراد النخامة ولحس المداد','698','234','0');
INSERT INTO [t96257] VALUES (252,'في الكحل، والسعوط للصائم، وما يجعل في الأذن، وما يستنشق، من دهن، أو بخور أو غيره','701','234','0');
INSERT INTO [t96257] VALUES (253,'في القىء، والقلس، والحقنة، والسواك، والحجامة للصائم','703','234','0');
INSERT INTO [t96257] VALUES (254,'في القبلة، والمباشرة، والنظر للصائم، والتذكر','705','234','0');
INSERT INTO [t96257] VALUES (255,'في الصائم يفطر ناسيا بوطء، أو طعام، أو تلذذ، أو طع عليه الفجر وهو يفعل ذلك ولا يعلم وكيف بالمظاهر، والمعتكف في ذلك','707','234','0');
INSERT INTO [t96257] VALUES (256,'في الكفارة في الفطر في رمضان، وما يوجبها','709','234','0');
INSERT INTO [t96257] VALUES (257,'في كفارة التفريط في قضاء رمضان','711','234','0');
INSERT INTO [t96257] VALUES (258,'في من عليه قضاء رمضان، هل يؤخره أو يبدئ عليه غيره أو يفرقه، ومن تعمد الفطر فيه، ومن لم يتعمد','713','234','0');
INSERT INTO [t96257] VALUES (259,'في متعمد الفطر، في قضاء التطوع، أو في قضاء رمضان، وفي مفسد قضاء الحج','715','234','0');
INSERT INTO [t96257] VALUES (260,'في من أفطر رمضان كله فقضى شهرا أقل عددا من أيامه أو أكثر','716','234','0');
INSERT INTO [t96257] VALUES (261,'في شهري الظهار، هل يبدأ فيهما من ذي القعدة، أو من شوال','716','234','0');
INSERT INTO [t96257] VALUES (262,'في من صام لظهارين فوصلهما، ثم ذكر يوما أو يومين','718','234','0');
INSERT INTO [t96257] VALUES (263,'في من لزمه شهران متتابعان فسافر، هل يفطر، وكيف إن مرض في سفره فأفطر، وكيف إن أفطر في الحضر لمرض، أو نسيان، أو لعذر، أو تعمد الفطر','719','234','0');
INSERT INTO [t96257] VALUES (264,'في من نذر صيام أيام بأعيانها فأفطر ناسيا، أو لعذر من مرض، أو لغيره، أو لسفر، وكيف إن أفطر عامدا','720','234','0');
INSERT INTO [t96257] VALUES (265,'في من نذر أن يصوم شهرا، أو عاما، بغير عينه، فبدأ في بعض الشهر، أو في أوله، وهل له أن يفرقه، وهل عليه قضاؤها في ذلك مما لا يصام، والعام بعينه، أو بغير عينه','722','234','0');
INSERT INTO [t96257] VALUES (266,'في من نذر صوم يوم، يقدم فيه فلان، أو يوم يقدم هو، أو نذر صيام يوم بعينه فأنسيه، أو نذر أن يصوم هذا اليوم شهرا، أو قال: هذا الشهر يوما','725','234','0');
INSERT INTO [t96257] VALUES (267,'جامع بقية مسائل النذور في الصوم','727','234','0');
INSERT INTO [t96257] VALUES (268,'في الصائم متطوعا، هل يفطر لقسم، أو لرضاء أبويه، أو يختار الفطر ليقضيه، هل له ذلك؟ أو لسفر، أو لغيره، وجامع الفطر في التطوع','729','234','0');
INSERT INTO [t96257] VALUES (269,'في صيام العبد تطوعا بغير إذن سيده، أو الحر بغير إذن أبويه، وصيام المرأة بغير إذن الزوج، مسلمة أو نصرانية','731','234','0');
INSERT INTO [t96257] VALUES (270,'في صيام أيام منى، ويوم عرفة، وعاشوراء، والأيام البيض','732','234','0');
INSERT INTO [t96257] VALUES (271,'جامع في صيام الأيام، والدهر، والوصال، وسرد الصيام، وهل يصوم أحد عن أحد','734','234','0');
INSERT INTO [t96257] VALUES (272,'ذكر بعض ما روي في فضل يوم رمضان، وقيامه، والنفقة فيه','737','234','0');
INSERT INTO [t96257] VALUES (273,'في الترغيب في صيام العشر، وعاشوراء، ويوم عرفة، ويوم منى، ويوم التروية، وأشهر الحرم، وشعبان، وشوال وإتباع رمضان بستة أيام منه','738','234','0');
INSERT INTO [t96257] VALUES (274,'جامع في فضل الصيام، وإخفائه، وما ينبغي من صون اللسان فيه، ومن فطر صائما','741','234','0');
INSERT INTO [t96257] VALUES (275,'كتاب الاعتكاف','745','0','0');
INSERT INTO [t96257] VALUES (276,'في عدد أيام الاعتكاف، وأقله، وهل يكون في غير المسجد، وأين يعتكف من المسجد، وهل يكره الاعتكاف لأحد','745','275','0');
INSERT INTO [t96257] VALUES (277,'ما يلزم من الصوم في الاعتكاف، والجوار، ومتى يدخل معتكفه، ومتى يخرج','747','275','0');
INSERT INTO [t96257] VALUES (278,'ما ينهى عنه المعتكف من الخروج ومن الأعمال','749','275','0');
INSERT INTO [t96257] VALUES (279,'ما ينتقض به الاعتكاف من الأحداث، وما له أن يخرج له وما ليس له','752','275','0');
INSERT INTO [t96257] VALUES (280,'في المعتكف يمرض والمعتكفة تحيض، أو تطلق، وكيف البناء في ذلك، والعمل','754','275','0');
INSERT INTO [t96257] VALUES (281,'ما يلزم من الاعتكاف بالنذر أو بالدخول فيه، ومن يلزمه - إذا مرض قضاؤه ومن لا يلزمه','756','275','0');
INSERT INTO [t96257] VALUES (282,'في الاعتكاف في الثغور ومن اعتكف في مسجد قرية، لا يجمع فيها','758','275','0');
INSERT INTO [t96257] VALUES (283,'باب ما جاء في ليلة القدر','760','275','0');
INSERT INTO [t96257] VALUES (284,'كتاب الزكاة','764','0','0');
INSERT INTO [t96257] VALUES (285,'ذكر ما يجب فيه الزكاة من العين، وغيره من الأنعام، والحبوب، والثمار، وما لا زكاة فيه','764','284','0');
INSERT INTO [t96257] VALUES (286,'في من له مائتا درهم، أو عشرون دينارا تنقص يسيرا أو كثيرا، وهي تجوز بجواز الوازنة، وكيف إن لم تجز، وهي تبلغ إذا صرفت ما فيه الزكاة','767','284','0');
INSERT INTO [t96257] VALUES (287,'في ما يجمع في الزكة من العين، والحب، والماشية وهل يخرج عن الورق ذهبا، أو عن الذهب ورقا','770','284','0');
INSERT INTO [t96257] VALUES (288,'في زكاة الحلي، وما يرصع منه بجوهر وما يحلى به السيف وغيره، وذكر آنية الذهب والفضة، وما يقتنى أو يتجر به من ذلك كله','772','284','0');
INSERT INTO [t96257] VALUES (289,'في الحلي، أو العروض تورث أو تقتنى أو يشترى وما تنقله النية إلى القنية، أو إلى التجارة من ذلك، وما لا تنقله، وما بيع بعد ذلك','776','284','0');
INSERT INTO [t96257] VALUES (290,'في زكاة الفائدة بسبب الميراث والهبات والصلات، وما يتأخر قبضه من ذلك، وفي قبض الوصي والوكيل','779','284','0');
INSERT INTO [t96257] VALUES (291,'في زكاة فائدة ما يؤخذ في صداق، أو دية أو غلة','782','284','0');
INSERT INTO [t96257] VALUES (292,'في زكاة فوائد الغلات؛ من المساكن، والعبيد، والحيوان، وغيرها، وغلة المشترى من المساكن، والمكترى منها، وما يؤاجر به المرء نفسه','784','284','0');
INSERT INTO [t96257] VALUES (293,'في زكاة الغلات وأثمانها وذكر ما يؤخذ فيها من عوض، أو في الأشياء الموروثة والمقتناة من ثمر، وكيف إن بيعت الثمار مع الرقاب أو بعد أن حرث، أو صوف الغنم','789','284','0');
INSERT INTO [t96257] VALUES (294,'في العبد يعتق والنصراني يسلم هل عليهما زكاة في مال أو ثمرة أو حب أو غير ذلك؟','793','284','0');
INSERT INTO [t96257] VALUES (295,'في زكاة مال المفقود والصبي والمجنون والأسير','793','284','0');
INSERT INTO [t96257] VALUES (296,'باب في زكاة المال اللقطة أو الوديعة أو المدفون أو المغصوب','794','284','0');
INSERT INTO [t96257] VALUES (297,'في زكاة المال يبضع أو يوهب أو يعزل لشراء قوت وكسوة','798','284','0');
INSERT INTO [t96257] VALUES (298,'في زكاة المال يفاد شيئا بعد شىء، وحكم الفوائد في أحوالها ونمائها، وما يضم منها بعضه إلى بعض','800','284','0');
INSERT INTO [t96257] VALUES (299,'في زكاة الدين وما يتفاوت قبضه منه، أو من بيع العرض، وزكاة ما يقارن ذلك من الفوائد باتفاق حول أو اختلافه، وزكاة الدين قبل قبضه، والعرض قبل بيعه','805','284','0');
INSERT INTO [t96257] VALUES (300,'في زكاة من عليه دين، وكيف إن كان عليه صداق، أو نفقة، أو زكاة فرط فيها، وذكر النفقات التى تلزمه','810','284','0');
INSERT INTO [t96257] VALUES (301,'في المديان هل يحسب ما عليه في دين له، أو فيما يقتنى من عروضه، أو في نصاب ماشيته، أو فيما له زكاة؛ من المعدن، أو الحب، أو مال ينفرد بحول أو في قيمة مكاتبه، ومدبره، والمعتق إلى أجل، والآبق، وشبه ذلك','814','284','0');
INSERT INTO [t96257] VALUES (302,'في من عليه دين فأحال به على دين له عند الحول، وفي المديان يوهب له الدين عند الحول، أو يحدث له ملك عرض يسواه قبل الحول أو بعده','818','284','0');
INSERT INTO [t96257] VALUES (303,'في زكاة ما ربح فيما لم ينقد فيه، أو فيما نقد بعض ثمنه، وفيما ابتاعه بدين، وفيما غصب ثمنه، أو تسلفه ونقده','820','284','0');
INSERT INTO [t96257] VALUES (304,'في زكاة أهل الإدارات','824','284','0');
INSERT INTO [t96257] VALUES (305,'في زكاة مال القراض','830','284','0');
INSERT INTO [t96257] VALUES (306,'في اشتراط الزكاة في القراض وفي المساقاة على أحدهما','833','284','0');
INSERT INTO [t96257] VALUES (307,'في الزكاة في مال القراض عن رقاب الغنم وعن العبيد في زكاة الفطر','834','284','0');
INSERT INTO [t96257] VALUES (308,'في زكاة القراض يتفاصلان فيه قبل الحول أو يتفاصلان بعد الحول، والمال بربحه عشرون دينارا أو أقل، أو يكون أحدهما عبدا أو نصرانيا أو مديانا','835','284','0');
INSERT INTO [t96257] VALUES (309,'في زكاة القراض يرجع إلى ربه بعضه قبل الحول، أو اشترى به أصولا، فباع الثمرة برأس المال، ثم باع الأصول','839','284','0');
INSERT INTO [t96257] VALUES (310,'في زكاة المال يعطى للرجل على أن له ربحه أو يحبس عليه، وزكاة المال يوقف للسلف','842','284','0');
INSERT INTO [t96257] VALUES (311,'في زكاة الأموال توقف لتفرق أعيانها، أو لتفرق غلتها، أو نسل الماشية أو ربح المال أو النخل تطعم ثمرتها سنين، والزرع يوصى به','844','284','0');
INSERT INTO [t96257] VALUES (312,'في من عجل إخراج زكاته أو أخرها، وفي الزكاة تتلف، وقد أخرجها، أو يتلف المال','847','284','0');
INSERT INTO [t96257] VALUES (313,'في الرجل يعرف بمنع زكاته','851','284','0');
INSERT INTO [t96257] VALUES (314,'في من مات وعليه زكاة، كما حلت، أو فرط فيها وقد أوصى بإخراجها أو لم يوص','852','284','0');
INSERT INTO [t96257] VALUES (315,'القول في المعادن وملكها وإقطاعها وأخذ الزكاة مما يخرج منها من ذهب أو ورق','855','284','0');
INSERT INTO [t96257] VALUES (316,'باب بقية القول في زكاة ما يخرج من المعدن من ذهب أو فضة','857','284','0');
INSERT INTO [t96257] VALUES (317,'جامع القول في الركاز','859','284','0');
INSERT INTO [t96257] VALUES (318,'فيما يؤخذ من أهل الذمة إذا تجروا إلى غير بلدهم','863','284','0');
INSERT INTO [t96257] VALUES (319,'فيما يؤخذ من الحربين إذا نزلوا عندنا للتجارة','866','284','0');
INSERT INTO [t96257] VALUES (320,'في الجزية','870','284','0');
INSERT INTO [t96257] VALUES (321,'كتاب زكاة الماشية والحب والفطر','871','0','0');
INSERT INTO [t96257] VALUES (322,'ذكر من أصول زكاة الماشية، وفي الإبل تزيد على عشرين ومائة أو يجب فيها أخذ سنين مختلفة','871','321','0');
INSERT INTO [t96257] VALUES (323,'ذكر أسنان ما يؤخذ في زكاة الماشية، وصفاتها من غنم أو إبل أو بقر','874','321','0');
INSERT INTO [t96257] VALUES (324,'تفسير الذود، والشنق، والوقص، والسائمة، والسخال، والفصلان، وغير ذلك مما يجري ذكره في الزكاة','876','321','0');
INSERT INTO [t96257] VALUES (325,'في من أعطى ئفضل مما عليه، وأخذ عوضا، أو دون ما عليه، وأدى عوضا، أو أعطى أفضل بغير عوض، أو معيبة وهي أثمن، وفي من كانت ماشيته عجافا كلها أو سخالا أو عجاجيل','877','321','0');
INSERT INTO [t96257] VALUES (326,'في من يؤدي في صدقته ثمنا أو يشتريها، أو يؤدى عن العين عرضا أو عن الحب عينا، وهل يشتري من الإمام شىء من الصدقة، أو يعطى لمديانه ما عليه','879','321','0');
INSERT INTO [t96257] VALUES (327,'ما يجمع من أصناف الماشية بعضه إلى بعض، أو من الحب، وفي من له أموال مفترقة في البلدان من ماشية أو حب','881','321','0');
INSERT INTO [t96257] VALUES (328,'في فائدة الماشية، وكيف إن نمت أو نقصت قبل الحول أو قبل مجىء الساعي','883','321','0');
INSERT INTO [t96257] VALUES (329,'في الغنم تباع قبل الحول وبعده بمال أو بجنسها أو بخلافها من الماشية، أو يقيل فها أو يبتاع بمال قبل حوله غنما، أو يبيع غنما بمال ثم يبتاع به غنما','885','321','0');
INSERT INTO [t96257] VALUES (330,'في من باع غنما ثم ردت عليه بعيب يعد حول، أو أخذها في تفليس المبتاع، وفي الساعي يأتي وقد قامت الغرماء','891','321','0');
INSERT INTO [t96257] VALUES (331,'في من تخلف عنه الساعي سنين، ثم أتاه وغنمه قد زادت أو نقصت، وهل يتخلف في سنة جدبة والغنم عجاف، وهل يؤخذ منها؟','893','321','0');
INSERT INTO [t96257] VALUES (332,'القول في الهارب عن الساعي','898','321','0');
INSERT INTO [t96257] VALUES (333,'في من لا يأتيه السعاة لبعده، وفي الأسر كيف يزكي','900','321','0');
INSERT INTO [t96257] VALUES (334,'في زكاة الخلطاء، وما يوجب الخلطة','901','321','0');
INSERT INTO [t96257] VALUES (335,'في الخلطة بجنسين مختلفين من الأنعام أو بشيئين مختلفين وتراددهما','904','321','0');
INSERT INTO [t96257] VALUES (336,'في ترادد الخليطين في اختلاف العدد وكيف إن كانا أو أحدهما لا زكاة في غنمه إن انفردت؟','905','321','0');
INSERT INTO [t96257] VALUES (337,'في الخليطين لأحدهما أو لكل واحد منهما غنم أخرى بخليط أو بغير خليط','911','321','0');
INSERT INTO [t96257] VALUES (338,'في من خالط عبده بغنم أو غيرها، وفي من وهب لابنه غنما فأراد عزلها في الصدقة أو يخالطه بها','913','321','0');
INSERT INTO [t96257] VALUES (339,'في سيرة السعاة في أخذ الصدقة، وهل يؤخذ بها أحد في غير بلده، وهل ينصب لها في الطرق، وفي تعدي المصدق؟','914','321','0');
INSERT INTO [t96257] VALUES (340,'في أرزاق السعاة، وهل يتضيفون بأحد أو يحملون على إبل الصدقة، وهل يليها العبد؟','916','321','0');
INSERT INTO [t96257] VALUES (341,'ما تجب فيه الزكاة من الحبوب والثمار وفي كم تجب من الكيل، أو ما يجمع منها من الزكاة، وفي الحائط فيه أصناف أو صنف','918','321','0');
INSERT INTO [t96257] VALUES (342,'في زكاة ما يسقى بالنضح وبماء السماء والعيون، وما يجمع من ثمر الشعاري أو من أرض العدو، وفي الأرض تزرع في السنة مرتين','921','321','0');
INSERT INTO [t96257] VALUES (343,'في خرص العنب والنخل، وكيف إن نقص أو زاد أو أجيح، وكيف بما أكل أو تصدق أو جد قبل طيبه؟','923','321','0');
INSERT INTO [t96257] VALUES (344,'فيما لايتزبب من العنب أو بلح لا يثمر، أو زيتون لا زيت فيه','925','321','0');
INSERT INTO [t96257] VALUES (345,'في عصر ما يزكي من زيته، وفي من باع زرعا أو حبا غيره قبل أن يزكيه، أو وهب ذلك أو تصدق به بأصله أو بغير أصله، وكيف إن أجيح المبيع','926','321','0');
INSERT INTO [t96257] VALUES (346,'في زكاة العرية، والهبة، وزكاة ما أوصى به من ثمرة أو زرع أو وهبه، أو تصدق به، أو انتزعه من عبده','928','321','0');
INSERT INTO [t96257] VALUES (347,'في دفع الزكاة إلى الإمام ممن يعدل أو لا يعدل، وهل يصدق الناس فيها؟ وكيف إن أخفى مها، أو أخرج جميعها بنفسه؟','933','321','0');
INSERT INTO [t96257] VALUES (348,'في وجه إخراج الصدقة في الأصناف الذين هم أهلها','937','321','0');
INSERT INTO [t96257] VALUES (349,'في قدر ما يعطي الرجل من الزكاة، وذكر الاجتهاد في قيمتها','943','321','0');
INSERT INTO [t96257] VALUES (350,'في إخراج الزكاة من بلد الى بلد','947','321','0');
INSERT INTO [t96257] VALUES (351,'في إعطاء الرجل أقاربه من الزكاة، وهل تعطى لأهل الأهواء؟','951','321','0');
INSERT INTO [t96257] VALUES (352,'في أخذ آل محمد النبي - صلى الله عليه وسلم - من الزكاة أو التطوع وذكر سهم ذي القربى من الفىء','953','321','0');
INSERT INTO [t96257] VALUES (353,'في قسم زكاة المعدن، وقسم الفىء والخمس، والجزية، والمال يجعل في السبيل، أو من وصية أو حبس','955','321','0');
INSERT INTO [t96257] VALUES (354,'في إلزام زكاة الفطر، وذكر مكيلها، وماذا يخرج من الحبوب، وهل يؤدى فيها ثمنا','957','321','0');
INSERT INTO [t96257] VALUES (355,'في الفقير هل يؤدى زكاة الفطر، وهل يأخذها؟','960','321','0');
INSERT INTO [t96257] VALUES (356,'فيمن عليه زكاة الفطر، ومن يلزم الرجل أن يؤدى عنه زكاة الفطر','962','321','0');
INSERT INTO [t96257] VALUES (357,'في ما يلزم أو يسقط من الفطرة، في من يموت أو يولد أو يسلم أو يباع أو يعتق أو يحتلم أو يطلق أو يبني أو يستغني ليلة الفطر أو يوم الفطر أو قبل دخول ليلته','964','321','0');
INSERT INTO [t96257] VALUES (358,'في زكاة الفطر عن عبد بين اثنين أو بعضه حر، أو عن العبد يرد بعيب أو لفساد بيع ليلة الفطر أو يومه، أو تأخذه الفطرة ولم يخرج من العهدة','966','321','0');
INSERT INTO [t96257] VALUES (359,'في زكاة الفطر عن عبيد القراض','969','321','0');
INSERT INTO [t96257] VALUES (360,'في دفع زكاة الفطر إلى الإمام أو تفريقها دونه، وهل تخرج من موضعها، وهل تخرج يوم الفطر وهل يأخذ منها من يليها','970','321','0');
INSERT INTO [t96257] VALUES (361,'مسائل من "كتاب الزكاة"، لابن سحنون من غير معاني الزكاة','971','321','0');
INSERT INTO [t96257] VALUES (362,'كتاب الحج','973','0','0');
INSERT INTO [t96257] VALUES (363,'في فريضة الحج، وذكر الاستطاعة والسبيل، وفي من وجده، وذكر استئذان الأبوين فيه، وذكر وجوب العمرة، ويوم الحج الأكبر','973','362','0');
INSERT INTO [t96257] VALUES (364,'في الغسل للإحرام، ولدخول مكة، ولوقوف عرفة، وذكر اغتسال المحرم لجنابة، أو لتبرد، أو لتطيب، أو لغير ذلك','978','362','0');
INSERT INTO [t96257] VALUES (365,'ما يجوز أن يفعله المحرم عند إحرامه قبل أن يحرم وعند إحلاله؛ من دهن أو إلقاء تفث أو تلبيد وغيره','983','362','0');
INSERT INTO [t96257] VALUES (366,'في الإحرام والتلبية والركوع عند الإحرام، وذكر النية وقطع التلبية','984','362','0');
INSERT INTO [t96257] VALUES (367,'ذكر المواقيت، ومن تعداها، وما يفعل من دخل المدينة، وذكر أشهر الحج والإحرام قبلها أو من وراء الميقات، أو من فاته الحج الى أين يحرم بالعمرة؟','990','362','0');
INSERT INTO [t96257] VALUES (368,'في لباس المحرم، وذكر ما فيه من صبغ أو طيب من الثياب، ومما يتوسد وينام عليه، وذكر العقد وشبهه في لباسه، وما فيه الفدية من اللباس','997','362','0');
INSERT INTO [t96257] VALUES (369,'في العقد والاحتزام والتعصيب وشبهه للمحرم، وتقليد السيف','1002','362','0');
INSERT INTO [t96257] VALUES (370,'في التظلل والتقبب للمحرم، وتغطية الرأس، وما يفعله من العقد ولصق الحرق ورباط المنطقة، ونحو ذلك','1004','362','0');
INSERT INTO [t96257] VALUES (371,'في الطيب للمحرم، وإلقاء التفث، وقتل الدواب، والدهن والكحل والحجامة وحلق الشعر والزينة، وغيرها، ومن فعله بغيره','1006','362','0');
INSERT INTO [t96257] VALUES (372,'ذكر فدية الإيذاء، وأين تذبح','1014','362','0');
INSERT INTO [t96257] VALUES (373,'باب في حج الصغير، والعبد، وذات الزوج، والمولى عليه، وما يدخل عليهم من جزاء أو فدية وفساد، والعمل عن الصبي','1014','362','0');
INSERT INTO [t96257] VALUES (374,'في العمرة، ووقها، وإيجابها','1018','362','0');
INSERT INTO [t96257] VALUES (375,'في القران والتمتع والإفراد، وإرداف الحج على العمرة، وما يلزم من قرن، أو تمتع، وما يدخل في ذلك من فساد أو فوات','1020','362','0');
INSERT INTO [t96257] VALUES (376,'في دخول المحرم مكة، وما يبدأ به، وذكر الطواف، والركوع، واستلام الأركان، والجنب في الطواف، والسعي بين الصفا والمروة، والسعى في المسيل، ومن صلى قبل أن يتم طوافه أو سعيه فرضا، أو نافلة، وذكر القراءة، والكلام، والجلوس في الطواف، وجامع القول فيه','1027','362','0');
INSERT INTO [t96257] VALUES (377,'في الطواف والسعي على غير طهر، ومن أحدث فيهما، أو طاف أو ركع بثوب نجس، والمرأة تحيض، وقد طافت أو لم تطف','1035','362','0');
INSERT INTO [t96257] VALUES (378,'في تأخير الطواف، وفي طواف المراهق والمكي، ومن طاف راكبا، أو محمولا، وفي تأخير السعي لمرض، أو غيره، ومن جلس أو وقف في طوافه أو سعيه','1037','362','0');
INSERT INTO [t96257] VALUES (379,'في الطواف بعد الصبح وبعد العصر، وكيف إن أخر الركعتين والسعي، وفي تأخير الطواف والركوع في الإفاضة','1039','362','0');
INSERT INTO [t96257] VALUES (380,'في من ذكر بعض طواف السعي أو الإفاضة، أو بعض السعي وقد رجع إلى بلده، أو لم يرجع، أو ذكر الركعتين، أو صلاهما في الحجر، وفي من طاف تطوعا، وعليه طواف واجب نسيه','1041','362','0');
INSERT INTO [t96257] VALUES (381,'في الخروج إلى منى، وإلى عرفة، والصلاة بها','1045','362','0');
INSERT INTO [t96257] VALUES (382,'في الوقوف بموقف عرفة والدفع منها، والمبيت بمزدلفة والدفع منها، والوقوف بالمشعر','1048','362','0');
INSERT INTO [t96257] VALUES (383,'جامع القول في رمي الجمار','1057','362','0');
INSERT INTO [t96257] VALUES (384,'جامع القول في الحلاق، والتقصير للحاج والمعتمر، وما يحل للحاج برمي الجمرة','1065','362','0');
INSERT INTO [t96257] VALUES (385,'في الإفاضة، والتعجل في يومين، وذكر المقام بمنى أيام منى، والمبيت بها، وذكر الصيد، وذكر المحصب','1070','362','0');
INSERT INTO [t96257] VALUES (386,'في قصر الحاج الصلاة بمنى، وذكر صلاة العيد والجمعة','1073','362','0');
INSERT INTO [t96257] VALUES (387,'في وطء المحرم وتلذذه، وما يفسد من ذلك حجه، أو عمرته، وكيف إن أكره أهله، وفي نكاحه ورجعته، وغسله امرأته، وكيف إن وطىء ثم أحرم','1075','362','0');
INSERT INTO [t96257] VALUES (388,'في من أفسد حجه قرانا أو متمتعا أو مفردا، أو فسد حجه ثم فاته، أو أفسد عمرته ثم تمتع، أو قضى حجا لفساده فأفسده، أو حج عن غيره أو لنذر فأفسد','1080','362','0');
INSERT INTO [t96257] VALUES (389,'في من فاته الحج، أو أحصره بعذر أو مرض، وفي المحرمة تحيض قبل الإفاضة، وذكر المستحاضة','1084','362','0');
INSERT INTO [t96257] VALUES (390,'في وداع البيت، وفي دخولها','1092','362','0');
INSERT INTO [t96257] VALUES (391,'في تقليد الهدي، وإشعاره، وتجليله، وإيقافه','1095','362','0');
INSERT INTO [t96257] VALUES (392,'في محل الهدي، وموضع النحر والذبح، وكيف تنحر البدن؟','1099','362','0');
INSERT INTO [t96257] VALUES (393,'فيما ضل أو عطب من الهدي قبل محله','1102','362','0');
INSERT INTO [t96257] VALUES (394,'في صفة النحر والذبح','1104','362','0');
INSERT INTO [t96257] VALUES (395,'ما يتقى من العيوب في الهدي، وما يرجع به من قيمة عيب أو رأس، وما يحدث في الهدي من عيب، أو عجف، وفي الهدي يباع','1105','362','0');
INSERT INTO [t96257] VALUES (396,'فيما يؤكل منه -من الهدي- وما يطعم منه ومن يطعم، وذكر ولد البدنة ولبنها والأكل مما عطب من الهدي، أو من بدل ما ضل منه','1107','362','0');
INSERT INTO [t96257] VALUES (397,'في الشركة في الهدي والأضحية، ومن أخطأ فنحر هدي غيره، وفي الهدي يختلط والأضحية','1111','362','0');
INSERT INTO [t96257] VALUES (398,'في من نذر هديا أو بدنة أو جزورا','1112','362','0');
INSERT INTO [t96257] VALUES (399,'في من لزمه هدي فلم يجده، أو تصدق به أو بثمنه، وفي صيام المتمتع والقارن وغيرهما، إذا لم يجد هديا، وفي هدي الفوات والفساد هل يعجل؟','1114','362','0');
INSERT INTO [t96257] VALUES (400,'ما يقتل المحرم من الدواب','1117','362','0');
INSERT INTO [t96257] VALUES (401,'في المحرم يأكل ما صيد من أجله، أو ما ذبحه الحلال، أو صاده المحرم','1121','362','0');
INSERT INTO [t96257] VALUES (402,'في الغصن في الحل وأصله في الحرم، هل يصاد ما عليه؟ وفي من رمى صيدا من الحل في الحرم، أو من الحرم في الحل، وفي إرسال الكلب كذلك','1129','362','0');
INSERT INTO [t96257] VALUES (403,'في ذبح ما دجن في الحرم للمحرم، وغيره، وفي بيض الطير وفراخه، وحمام مكة والحرم، وفي قطع شجر الحرم، وغيره، وذكر حرم المدينة، والصيد فيه','1131','362','0');
INSERT INTO [t96257] VALUES (404,'ذكر جزاء الصيد، وذكر الحكمين فيه، وأين يخرج الجزاء؟','1134','362','0');
INSERT INTO [t96257] VALUES (405,'في الوصية بالحج، وفي من حج عن غيره','1137','362','0');
INSERT INTO [t96257] VALUES (406,'في من أوجب على نفسه المشي إلى مكة في يمين، أو غير يمين','1148','362','0');
INSERT INTO [t96257] VALUES (407,'باب في ذكر البيت والصلاة فيه، وذكر الحجر والمقام وزمزم، وذكر الحرم ومعالمه، ومنى وعرفة، وذكر خطب الحج، وذكر منبر النبي - صلى الله عليه وسلم -، وحرم المدينة','1156','362','0');
INSERT INTO [t96257] VALUES (408,'باب جامع، وفيه ذكر القفل والمعرس','1160','362','0');
INSERT INTO [t96257] VALUES (409,'كتاب الجهاد','1163','0','0');
INSERT INTO [t96257] VALUES (410,'[الجزء الأول من الجهاد]','1163','409','0');
INSERT INTO [t96257] VALUES (411,'ذكر بعض ما روي في فضل الجهاد وذكر شيء من الرباط وفضله','1163','410','0');
INSERT INTO [t96257] VALUES (412,'ذكر بعض ما روي في فضل الرباط ولزوم الثغر، وفي الغارات، وذكر الأفضل من الحج والغزو والرباط، وذكر التكبير في الرباط، والحرس في لقاء العدو','1165','410','0');
INSERT INTO [t96257] VALUES (413,'ذكر فرض الجهاد وتطوعه وذكر النفير والهجرة','1170','410','0');
INSERT INTO [t96257] VALUES (414,'في الغزو والسفر بغير إذن الأبوين أو غيرهما، أو العبد بغير إذن سيده، ومن نذر الغزو ثم تخلف لعذر أو لغير عذر، وهل يغزو المديان أو من له قرابة؟','1173','410','0');
INSERT INTO [t96257] VALUES (415,'في الجهاد مع من لا يرضي من الولاة','1177','410','0');
INSERT INTO [t96257] VALUES (416,'في الغزو بغير إذن الإمام، وهل يسري أحد أو قاتل أو يبارز بغير إذنه، ومن يصلح في الإمارة في الحرب وطاعته','1178','410','0');
INSERT INTO [t96257] VALUES (417,'في سيرة الإمام في الغزو، وما ينبغي في سفره من القول والعمل، والسيرة في أرض العدو، وأدب الإمام ووصيته في الغزو، ومسيره ورفقه بالناس، وذكر ما وقف من دوابهم','1182','410','0');
INSERT INTO [t96257] VALUES (418,'في الغزو بالمصاحف والنساء والاستعانة بالكفار','1185','410','0');
INSERT INTO [t96257] VALUES (419,'في السرايا والألوة والرايات والعمائم، وفي خروج السرية بغير إذن الإمام','1187','410','0');
INSERT INTO [t96257] VALUES (420,'في اتخاذ السلاح والعدة، ورباط الخيل والنفقة عليها، وذكر الفروسية والرماية والعوم وزي العرب','1190','410','0');
INSERT INTO [t96257] VALUES (421,'في الدعوة قبل القتال','1192','410','0');
INSERT INTO [t96257] VALUES (422,'ذكر ما يدعو إليه وذكر من تقبل منه الجزية، وكيف إن بذلوا مالا على هدنة أو صلح، وهل تقبل الجزية من العرب؟','1195','410','0');
INSERT INTO [t96257] VALUES (423,'في لقاء العدو ووقت قتالهم والتعبئة للقتال والسكينة، وذكر الشعار ولباس الحرير وغيره في الحرب وتسويم الخيل، وفي من قاتل للفخر، والنية في الجهاد وطاعة الإمام في الفرار من الزحف والانحياز إلى فئة، وفيمن حصر فخاف الهلاك، ومن يلقي بنفسه من خوف النار إلى غرق أو قتل','1202','410','0');
INSERT INTO [t96257] VALUES (424,'في المبارزة وقتل الرجل ذا محارمه يذكر: الحرب خدعة، والقتل في الشهر الحرام','1206','410','0');
INSERT INTO [t96257] VALUES (425,'في قتل النساء والولدان والعسيف والشيخ الفاني وذي الزمانة، وكيف إن قاتلوا هم أو الرهبان','1209','410','0');
INSERT INTO [t96257] VALUES (426,'في الرهبان والنهي عن قتلهم، وهل يترك لهم أموالهم، والشيخ الكبير، وفي قتل الشمامسة، وهل تؤخذ الجزية ممن ترهب عندنا منهم؟','1212','410','0');
INSERT INTO [t96257] VALUES (427,'في إخراب بلد الحرب وقطع الشجر، وخراب أموالهم، وما يذبح لمأكله، وهل يحرق ما فضل من الغنيمة مما لا يطاق حمله','1215','410','0');
INSERT INTO [t96257] VALUES (428,'في رمي العدو بالنار والمجانيق وهل يغرقون أو يقطع عنهم الماء وشبه ذلك، أو يسم لهم الحديد أو الطعام، وفي طرح الحسك','1218','410','0');
INSERT INTO [t96257] VALUES (429,'في قتال الحبشة وشراء النوبة','1221','410','0');
INSERT INTO [t96257] VALUES (430,'في قتل الأسارى واسترقاقهم، والتمثيل بالعدو، وحمل الرؤوس وفداء الأسارى واسترقاقهم، ومن لا يقتل منهم، وفي أمان الأسير','1222','410','0');
INSERT INTO [t96257] VALUES (431,'في الأمان وذكر الإشارة بالأمان وما يجري من قول أو عمل فيظن أنه أمان، وما يكون منه على الخديعة','1227','410','0');
INSERT INTO [t96257] VALUES (432,'في أمان العبد والمرأة والصبي والمعاهد والمجنون وغيرهم، وكيف إن أمنهم أحد بعد أن نهى الإمام عن التأمين، ومن حكى الأمان عن غيره','1230','410','0');
INSERT INTO [t96257] VALUES (433,'في أمان الأسير من المسلمين بأيدي العدو، وأمان المكره من الأسارى، وأمان من خرج من طاعة الإمام من الخوارج','1234','410','0');
INSERT INTO [t96257] VALUES (434,'في تأمين الخوارج لأهل الكفر، أو استعانوا بهم علينا','1236','410','0');
INSERT INTO [t96257] VALUES (435,'في قتال الخوارج والحكم في أموالهم','1239','410','0');
INSERT INTO [t96257] VALUES (436,'في رجل من العسكر يؤمن العدو على مال أو على غير مال، وهل يقبل قوله في ذلك بعد خروجهم، وكيف إن اختلف فيه رجلان؟','1240','410','0');
INSERT INTO [t96257] VALUES (437,'الطاغبة، هل يصدق في نكث أو غيره؟','1241','410','0');
INSERT INTO [t96257] VALUES (438,'في المسلم يأتي إلى حصن يزعم أن الأمير أرسله إليهم بالأمان بكتاب يخترقه أو بغير كتاب، فصدقوه وفتحوا الحصن وقد كذب، وكيف إن جاء رسول من فيمن أمن على أن يدل على شيء أو يخبر بخبر، فلم يفعل أو خدعهم، أو أمنوا حصنا على أن يعرض عليهم الإسلام ليسلموا، ثم أبوا ذلك','1243','410','0');
INSERT INTO [t96257] VALUES (439,'في أهل الحصن يؤمنه أحد من أهل الجيش على مال أو على غير مال، أو يؤمن رجلا منهم على أن يدخل العسكر ويرجع، وكيف إن كان أمانا إلى أجل، وكيف إن كان الإمام قد قدم النهي عن ذلك','1247','410','0');
INSERT INTO [t96257] VALUES (440,'في السرية تؤمن الحصن، ثم تأتيهم سرية أخرى، وهل تدخل سرية فيما غنمت الأخرى، وذكر الدعوى في الصلح في ذلك','1248','410','0');
INSERT INTO [t96257] VALUES (441,'في المراوضة على الأمان والتداعي فيه، وهل يقبل فيه قول الرسل والوسائط، وهل يكون ما يترجم الترجمان يعمل عليه؟','1254','410','0');
INSERT INTO [t96257] VALUES (442,'باب في مسائل الأمان بمعان مختلفة','1257','410','0');
INSERT INTO [t96257] VALUES (443,'[الجزء الثاني من الجهاد]','1263','409','0');
INSERT INTO [t96257] VALUES (444,'باب آخر جامع لمسائل من معاني الأمان، وفي أمان الأسارى، والقول فيهم','1263','443','0');
INSERT INTO [t96257] VALUES (445,'في الأمان ومن يدخل فيه بالمعنى وإن لم يسم، وما يكون فداء من ذلك وما لا يكون فداء من الأموال ويدخل في الأمان','1266','443','0');
INSERT INTO [t96257] VALUES (446,'في الحصن يصالحهم الجيش على أن لا يسلكوا عليهم إذا رجعوا، أو على ألا يشربوا لهم ماء لا يفسدوا زرعا ولا يأكلوه لا يخربوا لهم قراهم ولا نأسرهم ولا نقتل أسراهم، وشرطوا لنا ذلك، وكيف إن خالفوا','1271','443','0');
INSERT INTO [t96257] VALUES (447,'في الصلح على أن لا يكتموا مالا أو لا يحدثوا حدثا ونحو ذلك، فيظهر عليهم خلاف ذلك، أو ينكث أحد منهم أو منا ما وقع به الصلح','1273','443','0');
INSERT INTO [t96257] VALUES (448,'في النزول بعهد على شرط لا يجوز، ومن نزل بشبهة أو بأثر عهد ذهبت مدته، أو زاد على شرط، أو لم يف به','1274','443','0');
INSERT INTO [t96257] VALUES (449,'فيمن وجد من العدو ببلد الحرب أو ببلد الإسلام أو في العسكر، فقال: جئت للأمان أو للذمة أو للفداء أو للإسلام، أو رسول وشبه هذا مما لم يوجف عليه','1276','443','0');
INSERT INTO [t96257] VALUES (450,'فيمن يوجد بساحلنا من مراكب العدو قد انكسرت أو لم تنكسر، فقالوا: نحن تجار، ونحو هذا وقاتلونا فظفرنا بهم','1282','443','0');
INSERT INTO [t96257] VALUES (451,'في الحربي ينزل عندنا بلا عهد فيتزوج، أو معاهد يتزوج ببلد الحرب، ما حكم ولده','1285','443','0');
INSERT INTO [t96257] VALUES (452,'فيمن دخل إلينا من الحربيين على المقام، هل له أن يرجع، ومن نزل على مقام مدة فجاوزها، وتجار الحربيين يركبون البحر من عندنا فيردهم الريح أو نحوه','1285','443','0');
INSERT INTO [t96257] VALUES (453,'في النازل على الأمان يظهر أنه مرتد أو عبد لمسلم أو رسول أو غير ما أظهر','1288','443','0');
INSERT INTO [t96257] VALUES (454,'في العدو ينزلون من الحصن على حكم الله أو على حكم فلان، وكيف إن نزلوا على ذلك أو حكم رجلين','1290','443','0');
INSERT INTO [t96257] VALUES (455,'باب ذكر ما يكون أمانا من غير شرط من قديم زوجة مع زوج مسلم أو ذمي، أو مع ذي محرم وغير ذلك، وكيف إن تداعيا ذلك واختلفا','1292','443','0');
INSERT INTO [t96257] VALUES (456,'فغي التداعي بين القادمين إلينا على عهد في استرقاق بعضهم بعضا أو في رقيق قدموا بهم','1293','443','0');
INSERT INTO [t96257] VALUES (457,'في المستأمن إلينا يسلم رقيقه أو يقدم بهم مسلمين، أو بيده رجل حر أو ذمي أو عبد مسلم، أو يجني جناية أو يحدث عندنا حدثا، وكيف إن مات والقول في ماله وديونه، وفي الحصن يصالحون على ما بأيديهم، فيوجد بأيديهم مسلمون','1296','443','0');
INSERT INTO [t96257] VALUES (458,'في المعاهد يريد أن يبيع منا زوجته وولده، أو صالحت عبدك على أن يأتيك بذلك','1305','443','0');
INSERT INTO [t96257] VALUES (459,'في المستأمنة تريد أن تتزوج عندنا','1306','443','0');
INSERT INTO [t96257] VALUES (460,'في سهمان الخيل وسهم الفارس والراجل، وذكر المريض والفرس الرهيص، والصغير والهرم وما أشبه ذلك','1309','443','0');
INSERT INTO [t96257] VALUES (461,'فيمن دخل أرض الحرب فارسا أو راجلا، ثم انتقل إلى خلاف ذلك من ركوب أو رجلة، وفيمن قاتل على فرس بعارية أو تعد أو شراء أو كراء، ومن مات بعد الإدراب','1312','443','0');
INSERT INTO [t96257] VALUES (462,'فيمن مات بعد الايجاف أو بعد القتال، ومن تخلف لمرض أو عذر أو ضل عن أصحابه أو بعث في أمر، وفي المراكب يردها الريح أو يغرقها','1319','443','0');
INSERT INTO [t96257] VALUES (463,'[الجزء الثالث من الجهاد]','1327','409','0');
INSERT INTO [t96257] VALUES (464,'فيما تغنمه السرية تتقدم أو تتأخر عن العسكر أو تضل، وهل تدخل إحدى السريتين في غنيمة الأخرى، وفي الجيش يغنم بعد انصراف طائفة منه','1327','463','0');
INSERT INTO [t96257] VALUES (465,'في سهمان الخيل في بلد الإسلام إذا أتى العدو إلى مدائنهم وحصونهم، وكيف إن قاتلهم بعضهم أو اتبعوهم في بر أو بحر، ولمن تكون الغنيمة منهم','1331','463','0');
INSERT INTO [t96257] VALUES (466,'فيمن يسهم له ممن لا يسهم من عبد وامرأة وصبي وأجير وتاجر وأسير وأمير الجيش وغيره، ومن ارتد بعد الغنيمة','1338','463','0');
INSERT INTO [t96257] VALUES (467,'في القسم للغائب والقتيل ومن أسر والمريض والضعيف ومن ضل','1344','463','0');
INSERT INTO [t96257] VALUES (468,'وجه كتابة الناس من فارس وراجل في قسم الغنيمة، وكيف تقسم وأين تقسم','1345','463','0');
INSERT INTO [t96257] VALUES (469,'في الغنائم والخمس وسهم ذي القربى، ومصارف الفيء والخمس','1348','463','0');
INSERT INTO [t96257] VALUES (470,'فيما يغنم العبيد وأهل الذمة من العدو في تلصص أو غير تلصص هل يخمس، وما يغنمه المرتدون، وفي العبد يصيب كنزا، ومن خرج من الحربيين إلى العسكر بمال، وكيف إن أسلم، أو كان عبدا، وكيف إن رجع','1351','463','0');
INSERT INTO [t96257] VALUES (471,'في العلول وفيمن غل من الغنيمة','1355','463','0');
INSERT INTO [t96257] VALUES (472,'فيما يصاب من الغنيمة من الطعام والماشية والعلف وغير ذلك، وكيف بما فضل له من ذلك، أو بيع منه، وما لا يكون غلولا','1356','463','0');
INSERT INTO [t96257] VALUES (473,'فيما يجوز أن ينصرف به معه ولا يدخل في المغانم، وما يدخل فيها مما يصنع أو مما لا يصنع، ومن الحيوان وغيره، وفي الكلب، وكتب الفقه هل تقسم، وفي كتب العدو، وما يوجد في قبورهم، وفيمن كسب من صنعة يده مالا بأرض العدو','1361','463','0');
INSERT INTO [t96257] VALUES (474,'فيما يضعف المسلمون عن حمله من الغنيمة ومن أموالهم، وكيف إن تركوه فمر به غيرهم، وهل له ترك مايقدر على حمله','1365','463','0');
INSERT INTO [t96257] VALUES (475,'في السرية يغنمون رقيقا هل يشتري منهم ولم يؤدوا الخمس','1367','463','0');
INSERT INTO [t96257] VALUES (476,'فيما يهديه العدو للخليفة أو لأمير الجيش أو لبعض الغزاة، هل يخمس؟','1368','463','0');
INSERT INTO [t96257] VALUES (477,'[الجزء الرابع من الجهاد]','1373','409','0');
INSERT INTO [t96257] VALUES (478,'جامع القول في الأنفال، وذكر السلب، وفي شرط النفل قبل الغنيمة','1373','477','0');
INSERT INTO [t96257] VALUES (479,'في تفريع مسائل نفل السلب وما يدخل في السلب، وذكر نفل الذهب والفضة','1378','477','0');
INSERT INTO [t96257] VALUES (480,'في المقاتل يجعل له السلب، أو غير السلب، فيقتل الإمام قتيلا','1381','477','0');
INSERT INTO [t96257] VALUES (481,'جامع القول في النفل يبذله الإمام قبل الغنيمة من جزء مسمى أو مال مسمى لمن قتل قتيلا أو لمن تقدم إلى الحصن، والقول في نفله للسرية، وفيما غنمت أو تغنم بعدها','1382','477','0');
INSERT INTO [t96257] VALUES (482,'في النفل على فعل شيء فيفعل بعضه أو ما يشبهه أو خلافه، وفي النفل لمن جاء من المال بكذا أو جاء بكذا فله كذا','1386','477','0');
INSERT INTO [t96257] VALUES (483,'في النقل في الذهب والفضة والعروض، وكيف إن استثنى شيئا، أو ذكر أشياء تنصرف إلى أصناف، وفي النفل في الأرض','1388','477','0');
INSERT INTO [t96257] VALUES (484,'في النفل المجهول','1392','477','0');
INSERT INTO [t96257] VALUES (485,'في الإمام ينفل السلب لمن قتل قتيلا، فيقتل الرجل من ينهى عن قتله أو يقتل عبدا','1394','477','0');
INSERT INTO [t96257] VALUES (486,'في السلب يحوزه المشركون وقد وجب للقاتل','1395','477','0');
INSERT INTO [t96257] VALUES (487,'في التداعي في السلب وجامع القضاء فيه','1396','477','0');
INSERT INTO [t96257] VALUES (488,'في الأمير ينفل، ثم يعزل أو يموت، أو يموت أحد ممن نفل، أو يلحق بالعسكر قوم أسلموا','1399','477','0');
INSERT INTO [t96257] VALUES (489,'في الغنيمة فيها شرط نفل هل يقتل منها الأسارى، وكيف إن استهلك أحد من تلك الغنيمة شيئا','1400','477','0');
INSERT INTO [t96257] VALUES (490,'في السرية ينفلها أمير الجيش أو أمير السرية، وهل ينفل بعض السرية، والسرية تنقطع عن الجيش','1401','477','0');
INSERT INTO [t96257] VALUES (491,'في الحكم في النفل والغنيمة في دخول عسكر على عسكر وسرية على سرية، أو يرجع الأمير على ما نفل','1402','477','0');
INSERT INTO [t96257] VALUES (492,'في الأمير ينفل جميع الغنيمة أو يقول: من أصاب شيئا فهو له، والمنفول يعتق بعض عبد، وكيف إن كان في النفل من يعتق عليه','1404','477','0');
INSERT INTO [t96257] VALUES (493,'في النفل هل يكون لأهل الذمة أو لامرأة، وفي الأمير ينفل لا يعلم بذلك بعض الجيش','1405','477','0');
INSERT INTO [t96257] VALUES (494,'في الإمام يقول: من قتل قتيلا فله فرسه، أو قال: فرسا، وكان تحت المقتول برذون أو حمار أو بعير أو نحوه، وكيف إن قال: من قاتل على فرس فله كذا، فقاتل على برذون','1406','477','0');
INSERT INTO [t96257] VALUES (495,'في الحكم فيما يوجد في الغنيمة من مال مسلم أو ذمي، وكيف إن كان عبدا فبيع أو أسر ثانية ثم غنم، أو كان جنى جناية أو كان مرهونا','1407','477','0');
INSERT INTO [t96257] VALUES (496,'فيمن اشترى أمة من المغنم أو فداها من العدو أو وهبت له، ثم أحدث فيها عتقها أو أولدها أو باعها، وهل يصدق فيما فداها به','1414','477','0');
INSERT INTO [t96257] VALUES (497,'في الفرس والسيف يوجد في المغنم وفيه مكتوب: حبس، وكيف إن باعه ربه وذلك فيه، وفي النبل يوجد في المعركة','1415','477','0');
INSERT INTO [t96257] VALUES (498,'في أم الولد تقع في المقاسم فتشتري أو تفدي من العدو','1416','477','0');
INSERT INTO [t96257] VALUES (499,'في المدبر يقع في المغانم أو يفدي من العدو أو يسلمون عليه، وكيف إن أحدث فيه من فداه تدبيرا أو عتقا، وما دخل فيه من ذكر المكاتب والمعتق إلى أجل','1420','477','0');
INSERT INTO [t96257] VALUES (500,'في المدبر يجني، ثم يؤسر، ثم يقع في المقاسم','1424','477','0');
INSERT INTO [t96257] VALUES (501,'في المعتق الى أجل يشترى من المغانم أو من العدو أو يسلم عليه أحد','1425','477','0');
INSERT INTO [t96257] VALUES (502,'في المكاتب يسبي، ثم يغنم فيقع في سهم رجل، أو يفديه من العدو، أو يسلم عليه حربي وفي العبد في المغنم يدعي أنه مكاتب أو مدبر أو معتق إلى أجل ونحو ذلك','1426','477','0');
INSERT INTO [t96257] VALUES (503,'في الخدم يشتري من العدو','1428','477','0');
INSERT INTO [t96257] VALUES (504,'فيمن ابتاع عبدا فغنمه العدو قبل دفع ثمنه، ثم غنمناه فصار في سهم رجل','1428','477','0');
INSERT INTO [t96257] VALUES (505,'في الحر المسلم أو الذمي يفدي من العدو أو يقع في المقاسم، أو يسلم عليه حربي، كيف بمن بعضه حر وبعضه رقيق أو عبد أسر فأعتقه ربه ثم غنمناه، أو أعتقه ربا، ببلد الحرب، أو أسلم نصراني ببلاده ثم غنمناه','1429','477','0');
INSERT INTO [t96257] VALUES (506,'في الحرة أو الأمة أو الذمية تسبى فتوطأ فتلد، ثم ظهرنا عليهم بغنيمة أو خرجوا إلينا، والحربي يسلم ويقدم إلينا أو لا يقدم، ثم نظهر نحن على بلاده، ما حكم ماله وأهله وولده أو مسلم تزوج عندهم','1433','477','0');
INSERT INTO [t96257] VALUES (507,'فيمن أسلم من عبيد أهل الحرب، ثم قدم أو غنمناه، أو قدم ثم أسلم','1435','477','0');
INSERT INTO [t96257] VALUES (508,'في العلج يباع في المغنم فيوجد معه مال أو له ببلده أو ببلدنا','1437','477','0');
INSERT INTO [t96257] VALUES (509,'في العتق من المغنم، كيف إن كان في المغنم من يعتق على بعض أهل المعنم، وفي الوطء والسرقة من المغنم','1437','477','0');
INSERT INTO [t96257] VALUES (510,'في تعارف السبي بين الزوجين والأقارب والتفرقة بينهم، يما يحل به وطء سبي النساء','1439','477','0');
