INSERT INTO [t96257] VALUES (2501,'فيمن أخدم عبده ثم دبرها أو كاتب عبده ثم دبره، أو دبره ثم كاتبه، أو قاطعه على مال، أو أعتقه الى أجل أو قبل موته بشهر أو بعده، أو قبل موت العبد بشهر وشبه ذلك','6089','2497','0');
INSERT INTO [t96257] VALUES (2502,'في المدبر يكاتب ثم يموت السيد وعليه دين يغترق الرقبة أو بعضها أو بعض الكتابة','6090','2497','0');
INSERT INTO [t96257] VALUES (2503,'ذكر ما بوجب التدبير من اللفظ وفيمن قال لعبده أنت مدبر، وقال إن مت أو لم يقل، أو قال أنت حر بعد موتي، وكيف إن قال أنت مدبر عن ابني','6091','2497','0');
INSERT INTO [t96257] VALUES (2504,'فيمن باع عبدا من رجل على أن يدبره أو يعتقه، أو أمة على أن يتخذها أم ولد، أو أخذ مالا على أن يدبر عبده','6094','2497','0');
INSERT INTO [t96257] VALUES (2505,'في تدبر المولي عليه وذات الزوج والنصراني يدبره عبده المسلم أو يسلم وهو مدبر، وفي العبد ومن فيه بقية رق يدبر أمته بإذن السيد أو بغير إذن، وهل يطؤها أحدهما','6095','2497','0');
INSERT INTO [t96257] VALUES (2506,'فيمن دبر شقصا له من عبد أو بعض عبد له أو دبر جميع عبد له أو بعضه، والمدبر بين رجلين يعتق أحدهما نصيبه','6097','2497','0');
INSERT INTO [t96257] VALUES (2507,'في المدبر لا يخرج من ثك ما حضر، وللميت مال غائب، أو دبر على أجنبي، أو على الوارث وكيف إن بيع ما رق منه ثم حضر مال؟ وكيف إن أبق المدبر؟','6103','2497','0');
INSERT INTO [t96257] VALUES (2508,'فيمن دبر عبيدا بعضهم قبل بعض وكيف يبدؤون في الثلث، وكيف إن كان عليه دين أو أوصى بوصايا، والحكم في ولد المدبرة وذكر التفرقة','6108','2497','0');
INSERT INTO [t96257] VALUES (2509,'في القول في مال المدبر في حياة سيده وبعد مماته، وهل يقوم به؟، ومن دبر عبده واشترط ماله بعد الموت وغير ذلك من ذكر ماله','6112','2497','0');
INSERT INTO [t96257] VALUES (2510,'في جناية المدبر والجناية عليه','6113','2497','0');
INSERT INTO [t96257] VALUES (2511,'كتاب الخدمة','6115','0','0');
INSERT INTO [t96257] VALUES (2512,'في العبد الخدم عمرا أو أجلا ومرجعه إلى حريته بعد موت سيده أو موت غيره وما يكون من ذلك من الثلث','6115','2511','0');
INSERT INTO [t96257] VALUES (2513,'فيمن أخدم عبده رجلين ثم هو حر فمات أحدهما ونحوه، وكيف إن اشترى أحدهما خدمة الآخر أو وضع حصته من الخدمة أو أعتق بحصته؟','6120','2511','0');
INSERT INTO [t96257] VALUES (2514,'في المخدم يضع خدمته للعبد أو يبيعها من سيد من هذا المعنى أو في بيع الرقبة من المخدم، وفي بيع الشريك نصيبه، وقد خدم شريكه حصته أو وهبها وغير ذلك من مسائل الخدمة','6122','2511','0');
INSERT INTO [t96257] VALUES (2515,'فيمن أخدم عبده، أو أوصى بذلك ولم يؤجل، أو قال اخدمني وأنت حر ولم يؤجل، أو قال اخدم فلانا حتى يبلغ ابنه أو قال حتى يستغني أو ينكح أو قال بحصته ثم هو حر، أو أجل أجلا فمات الصبي قبله','6125','2511','0');
INSERT INTO [t96257] VALUES (2516,'في الحكم في المخدمة في نفقتها ومالها وولدها وديتها وميراثها وكيف إن أبقت؟ وفي المؤاجرة، وكيف إن اشترط إن أبقت فلا عتق لكم؟','6128','2511','0');
INSERT INTO [t96257] VALUES (2517,'في المخدمة يطؤها أو يتزوجها أو من له مرجعها، وكيف إن حملت؟','6130','2511','0');
INSERT INTO [t96257] VALUES (2518,'فيمن أوصى بخدمة عبده ثم يعتق أو لا يعتق، وأوصى بوصايا','6132','2511','0');
INSERT INTO [t96257] VALUES (2519,'في جناية العبد المخدم على المخدم أو غيره','6135','2511','0');
INSERT INTO [t96257] VALUES (2520,'كتاب المكاتب','6137','0','0');
INSERT INTO [t96257] VALUES (2521,'في التحضيض على الكتابة، وهل يكره عليها العبد؟ والوضعية منها، و١ذكر كتابة الصغير ومن لا حرفة له','6137','2520','0');
INSERT INTO [t96257] VALUES (2522,'في المكاتب يشترط عليه مع المال ضحايا (وأسفار) وخدمة ثم يؤدي قبل ذلك، والمكاتب يختدم فيما عليه بشرط وبغير شرط، أو شرط أن لا يعتق حتى يخدم','6139','2520','0');
INSERT INTO [t96257] VALUES (2523,'في القائل لعبده إن جئتني بكذا فأنت حر، أو يوصي بذلك، هل هو كالكتابة؟ وفيما يحدث بعد ذلك من ولد','6141','2520','0');
INSERT INTO [t96257] VALUES (2524,'جامع الشروط المكروهة أو الجائزة في الكتابة، والخيار في الكتابة، وفيمن أعتق أمة على أن تنكحه','6142','2520','0');
INSERT INTO [t96257] VALUES (2525,'في الكتابة على غرر أو مجهول أو على عبد فلان أو على شيء لم يوصف، أو على أن يأتي فأبق، أو يقوم على غنم أو بقر فإذا صارت كذا فهو حر، أو على أن يغرس نخلا','6145','2520','0');
INSERT INTO [t96257] VALUES (2526,'في الرهن في الكتابة والحمالة فيها','6147','2520','0');
INSERT INTO [t96257] VALUES (2527,'في كتابة المدبر والمعتق إلى أجل، وكيف إن مات السيد وعليه دين؟ ومن دبر مكاتبه، وفي النصراني يكاتب أم ولده، وفي المكاتب يدبر أمته بإذن سيده','6148','2520','0');
INSERT INTO [t96257] VALUES (2528,'في سفر المكاتب وتزويجه وإعتاقه عبده بغير إذن سيده، وهل يزوج عبده أو أمته؟، وهل يزوج الرجل مكاتبته أو يتزوجها، أو يعتق عبد مكاتبه؟','6149','2520','0');
INSERT INTO [t96257] VALUES (2529,'في تعجيز المكاتب وأدائه والتلوم له ومن أعتق مكاتبه إلى أجل','6150','2520','0');
INSERT INTO [t96257] VALUES (2530,'في اجتماع العبيد في كتابة واحدة، وذكر حمالتهم وتراجعهم في الأداء بعضهم على بعض وسعيهم، وفي بيع بعضهم ومكاتبة العبد مع أمته','6152','2520','0');
INSERT INTO [t96257] VALUES (2531,'في العبد بين الشركاء يكاتبه أحدهم ثم يقاطعه ومن كاتب عبده وعليه وعلى أم ولده','6155','2520','0');
INSERT INTO [t96257] VALUES (2532,'فيمن يدخل في الكتابة بالولادة أو الشراء، وفي موت المكاتب، وفي ميراث المكاتب، وفي موت أحد المكاتبين، وفي أم الولد يموت عنها وتخاف العجز وكيف إن باعها حاملا؟','6156','2520','0');
INSERT INTO [t96257] VALUES (2533,'في المكاتبين في كتابة واحدة يعتق بعضهم، أو يحنث فيه بعتق، أو كاتب عبدا مع مدبره، أو يستحق أحدهم','6161','2520','0');
INSERT INTO [t96257] VALUES (2534,'فيمن أعتق بعض مكاتبه، أو شقصا له فيه في حياته أو في وصيته، أو وضع عنه من كتابته أو أعتقه في وصية','6162','2520','0');
INSERT INTO [t96257] VALUES (2535,'فيمن وهب لرجل مكاتبه أو بعضه أو كتابته أو بعضها، أو نجما منها في حياته أو في وصيته','6166','2520','0');
INSERT INTO [t96257] VALUES (2536,'في بيع المكاتب أو الكتابة أو بعضها، وفي ميراث المكاتب بعد ذلك، وفي المكاتب يقع في المقاسم','6171','2520','0');
INSERT INTO [t96257] VALUES (2537,'فيمن اشترى كتابة ولده، أو أوصي له بذلك، أو بثمن ابنه، وفي المكاتب يشتري من يعتق عليه أو على سيده','6173','2520','0');
INSERT INTO [t96257] VALUES (2538,'فيمن كاتب عبده في مرضه أو أوصى أن يكاتب أو يعتق بعضه ويكاتب بعضه، أو أقر بكتابة مكاتب، أو أوصى أن يعتق عبده بعد قبض مال أو حضانة','6174','2520','0');
INSERT INTO [t96257] VALUES (2539,'فيمن وطيء مكاتبته فحملت، أو مكاتبة لأبيه أو لمكاتبه، أو أمة لمكاتبه، أو أم ولد له، وهل يطأ المكاتب أم ولد أدخلت معه في الكتابة؟','6176','2520','0');
INSERT INTO [t96257] VALUES (2540,'في قطاعة المكاتب، وما يجوز في الكتابة من معاوضة وحوالة وحمالة وشيء من ذكر التداعي في القطاعة أو في مال الكتابة','6181','2520','0');
INSERT INTO [t96257] VALUES (2541,'في قطاعة المكاتب بين الشركاء','6183','2520','0');
INSERT INTO [t96257] VALUES (2542,'في إنظار أحد الشريكين وتبدية أحدهما بالأداء، أو سلعة له، وهل يقتسمان عليه؟','6185','2520','0');
INSERT INTO [t96257] VALUES (2543,'في المكاتب يؤدي ثم يستحق بملك أو بحرية، ومن جحد عبده العتق ثم استغل أو وطيء','6189','2520','0');
INSERT INTO [t96257] VALUES (2544,'فيمن ودى كتابته أو بعضها أو قطاعة ثم استحق ما دفع','6191','2520','0');
INSERT INTO [t96257] VALUES (2545,'في جناية المكاتب وجناية سيده عليه ووطئه لمكاتبته غصبا وهذا الباب موعب في كتاب الجنايات بما فيه الكفاية','6192','2520','0');
INSERT INTO [t96257] VALUES (2546,'في ردة المكاتب، وفي نصراني كاتب عبده على خمر ثم أسلم أحدهما','6193','2520','0');
INSERT INTO [t96257] VALUES (2547,'مسائل مختلفة من المكاتب','6194','2520','0');
INSERT INTO [t96257] VALUES (2548,'كتاب أمهات الأولاد','6195','0','0');
INSERT INTO [t96257] VALUES (2549,'في الحجة في منع بيع أمهات الأولاد','6195','2548','0');
INSERT INTO [t96257] VALUES (2550,'ما تكون به الأمة أم ولد، وهل يتم حرمتها بعد موت السيد قبل أن تضع؟ وفيمن أقر بالوطء ثم جاءت بولد أو شك في الاستبراء؟ وذكر العزل عن الحرة، وكيف إن قال كنت أعزل وقد استبرأت؟','6197','2548','0');
INSERT INTO [t96257] VALUES (2551,'في الأمة الحامل تلد إن ملكها زوجها، وهو ولد السيد أو أجنبي، أو بعد أن أعتق سيدها الذى أحبلها، هل تكون به أم ولد؟','6201','2548','0');
INSERT INTO [t96257] VALUES (2552,'ما تكون به الأمة أم ولد من وطء الشبة من إحلال، أو غلط أو تأويل أو مخدمة أو غير ذلك، وما لا تكون به أم ولد مما ليس بشبهة','6202','2548','0');
INSERT INTO [t96257] VALUES (2553,'في أم ولد المكاتب والمدبر والمعتق إلى أجل والمعتق بعضه والعبد، ومن وطيء أم ولد مكاتبه أو مكاتبته أو مدبره فحملت','6206','2548','0');
INSERT INTO [t96257] VALUES (2554,'في أم ولد الذمي أو مكاتبه أو ولد أم ولده يسلم أحدهما، وهل له بيع أم ولده وهما نصرانيان،؟ وفي أم ولد المرتد، وفي جناية أم ولده ومدبره ومكاتبه','6209','2548','0');
INSERT INTO [t96257] VALUES (2555,'في الإقرار بالولد لا يعرف له نسب، أو يولد في ملكه أو بعد بيعه أو بيع أمه، أو أقر في صحته أو في مرضه، أو أقر في أمة أنها ولدت منه ومعها ولد، لا ولد معها وذكر اللقيط','6212','2548','0');
INSERT INTO [t96257] VALUES (2556,'في الأمة يطؤها السيد أو مبتاعها منه، وهي ذات زوج، أو في عدة منه فتأتي بولد وفي الولد يدعيه السيد والزوج أو الأب والابن','6220','2548','0');
INSERT INTO [t96257] VALUES (2557,'في الأمة بين الشريكين يطؤها أحدهما فتحمل، أو لا تحمل','6222','2548','0');
INSERT INTO [t96257] VALUES (2558,'في الأمة بين الرجلين يعتق أحدهما نصيبه فيها، ثم تحمل من الآخر أو من مبتاع منه، أو تدعي ولدا أو تحمل منه وبعضها حر','6225','2548','0');
INSERT INTO [t96257] VALUES (2559,'في الأمة بين الشريكين تلد من يطء أحدهما، ثم يولدها الآخر، وكيف إن كان؟','6228','2548','0');
INSERT INTO [t96257] VALUES (2560,'في المدبرة أو المكاتبة أو المعتقة إلى أجل بين الشريكين تلد من وطء أحدهما، وكيف أن ولدت من الآخر أيضا؟','6232','2548','0');
INSERT INTO [t96257] VALUES (2561,'في الأمة تأتي بولد من وطء الشريكين ومن وطء المتبايعين','6236','2548','0');
INSERT INTO [t96257] VALUES (2562,'في الأمة بين الحر والعبد يطآنها في طهر، أو أحدهما، فتأتي بولد','6243','2548','0');
INSERT INTO [t96257] VALUES (2563,'في الأب يطأ أمة الولد أو مدبرته، أو أم ولده، فتحمل أو لا تحمل، وكيف إن كان أحدهما عبدا، أو الابن يطأ أمة من في كتابته فتحمل، ويدعي أخوه أنه من أبيه','6245','2548','0');
INSERT INTO [t96257] VALUES (2564,'في الأمة أو من فيها بقية رق تستحق وقد ولدت من مشتر أو زوج، والقضاء في ولدها','6249','2548','0');
INSERT INTO [t96257] VALUES (2565,'فيمن ادعى في أمة أنه ابتاعها أو تزوجها والسيد منكر أو مقر بغير الوجه الذي ادعاه من نكاح أو بيع أو ابتياع وقد ولدت، أو يبتاعها الزوج ثم يختلفان في الولد متي ولدته','6252','2548','0');
INSERT INTO [t96257] VALUES (2566,'فيمن باع أم ولده، وكيف إن أعتقت أو ولدت ومن حلف ألا يطأها هل تعتق عليه؟','6256','2548','0');
INSERT INTO [t96257] VALUES (2567,'جامع القول في ولد أم الولد','6258','2548','0');
INSERT INTO [t96257] VALUES (2568,'مسائل مختلفة من أمهات الأولاد','6259','2548','0');
INSERT INTO [t96257] VALUES (2569,'في أم الولد يموت السيد وبيدها حلي أو ثياب وفرش، هل تكون لها؟ وكيف بما أوصى لها به من ذلك؟','6259','2548','0');
INSERT INTO [t96257] VALUES (2570,'كتاب الاستلحاق والإقرار بالنسب','6261','0','0');
INSERT INTO [t96257] VALUES (2571,'جامع القول في الاستلحاق والإقرار بالولد وغيره ومن أقر بأخ هل يرثه؟ أو قال فلان وارثي هل يرثه؟','6261','2570','0');
INSERT INTO [t96257] VALUES (2572,'فيما يستلحق من الولد بعد بيعه، أو بيع الأم، وفي الأمة لها زوج، فيستلحق السيد ولدها، وفي الولد يستحلق بعد اللعان','6266','2570','0');
INSERT INTO [t96257] VALUES (2573,'فيمن أقر في أولاد أمته أن أحدهم ولده، أو أقر بأحدهم بعينه عند بينة ونسيته البينة أو اختلط ولد أمة وحرة','6272','2570','0');
INSERT INTO [t96257] VALUES (2574,'فيمن شهد عليه شاهد أنه أقر أنه أولد جاريته هذه هذا الولد، وشهد آخر بإقراره أنه أولدها ولدا آخر غيره بعده','6277','2570','0');
INSERT INTO [t96257] VALUES (2575,'فيمن استلحق ولدا ثم أنكره','6278','2570','0');
INSERT INTO [t96257] VALUES (2576,'في توارث المتحملين من أهل الكفر وغير المتحملين ممن أسلم، وأهل العتق هل يتوارثون؟','6279','2570','0');
INSERT INTO [t96257] VALUES (2577,'في توأم المتحملة وتوأم الزانية والملاعنة والمغتصبة','6280','2570','0');
INSERT INTO [t96257] VALUES (2578,'في الإقرار بالنسب أو بالقرابة أو بالزوجية','6281','2570','0');
INSERT INTO [t96257] VALUES (2579,'فيمن أقر بأخ في صحته هل يوارثه؟','6283','2570','0');
INSERT INTO [t96257] VALUES (2580,'فيمن ترك ولدا أو ترك أولادا لإمائه فقال ولده هذا أخي لا بل هذا أخي لا بل هذا، أو قال ذلك في أجنبين','6284','2570','0');
INSERT INTO [t96257] VALUES (2581,'إلحاق النسب والقافة','6285','2570','0');
INSERT INTO [t96257] VALUES (2582,'في الدعوى في النسب والقرابة والزوجة وبماذا يثبت ذلك؟','6285','2570','0');
INSERT INTO [t96257] VALUES (2583,'في إقرار بعض الورثة بوارث، أو يقر أن الميت أقر بوارث له','6286','2570','0');
INSERT INTO [t96257] VALUES (2584,'في إقرار أحد الورثة بوارث، وفي الفريضة عول، أو تكون على الإقرار عائلة','6290','2570','0');
INSERT INTO [t96257] VALUES (2585,'في إقرار بعض الورثة بوارث في المناسخة، وبعد موت بعض الورثة','6293','2570','0');
INSERT INTO [t96257] VALUES (2586,'في الوارث يقر بوارث من وجه، ويقر به وارث غيره من وجه آخر، فيصدق أحدهما أو يدعي غير الوجه الذى أقر له به المقر','6296','2570','0');
INSERT INTO [t96257] VALUES (2587,'في أحدم الورثة يقر بوارث، ويقر بوارث آخر','6299','2570','0');
INSERT INTO [t96257] VALUES (2588,'فيمن ترك ولدين، فأقر أحدهما بأخوين، فصدقه أخوه بأحدهما','6303','2570','0');
INSERT INTO [t96257] VALUES (2589,'فيمن أقر بوارث معه ثم أقر بآخر بعد أن دفع إلى الأول مورثه أو قبل','6304','2570','0');
INSERT INTO [t96257] VALUES (2590,'في أحد الولدين يقر بأخ بعد أن قاسم أخاه والتركة عين أو عرض','6305','2570','0');
INSERT INTO [t96257] VALUES (2591,'فيمن ترك ولدين، فأقر أحدهما بأخ، ثم مات المقر، أو المقر به، أو كانوا ثلاثة','6306','2570','0');
INSERT INTO [t96257] VALUES (2592,'في الورثة يقرون بوارث فيقول المقر به عندي نصيبي','6307','2570','0');
INSERT INTO [t96257] VALUES (2593,'كتاب الولاء','6311','0','0');
INSERT INTO [t96257] VALUES (2594,'ذكر الولاء فيما أعتق الرجل عن نفسه، أو عن غيره، أو دبر عن غيره، أو أعتق مدبره أو مكاتبه في نفسه، أو عن غيره، وفي عتق السائبة ومن الزكاة','6311','2593','0');
INSERT INTO [t96257] VALUES (2595,'في جر الولاء ولد العبد من نكاح حرة','6315','2593','0');
