INSERT INTO [t96257] VALUES (2598,'في جر البنات ولاء من عتق، وهل يرثن الولاء؟','6326','2593','0');
INSERT INTO [t96257] VALUES (2599,'في ولاء النصراني يعتقه المسلم، وميراثه وميراث الذمي الحر لا وارث له','6331','2593','0');
INSERT INTO [t96257] VALUES (2600,'في نصراني عبد لمسلم أعتق مسلما ثم عتق ثم مات المسلم المعتوق','6332','2593','0');
INSERT INTO [t96257] VALUES (2601,'في النصراني العربي يعتق نصرانيا ثم يسلم ثم يجني','6333','2593','0');
INSERT INTO [t96257] VALUES (2602,'في ولاء النصراني ينقض العهد ثم يسبي مرة ثانية، ولمن ولاء من أعتق أو ولد له قبل ذلك أو بعده؟','6334','2593','0');
INSERT INTO [t96257] VALUES (2603,'في الحربي يعتق عبده، أو يسلم أحدهما ثم يقدم أحدهما أو يسبى','6337','2593','0');
INSERT INTO [t96257] VALUES (2604,'فيمن ارتد ثم تاب، وقد كان أعتق أو عقد عتقا من تدبير ونحوه وفي العبد يرتد','6340','2593','0');
INSERT INTO [t96257] VALUES (2605,'فيمن أعتقه رجل وأعتق أباه رجل آخر كيف يكتب ولاؤه؟ أو الرجل يعتقه الرجلان، ومن لا يعرف له نسب، أو نصراني أسلم كيف يكتب نسبه؟ وولاء من أسلم على يديك','6340','2593','0');
INSERT INTO [t96257] VALUES (2606,'في الكافر يسلم بعض ولده بعد موته، ومن أسلم ثم مات عن أولاد صغار ولدوا في نصرانيته، وآخرون بعد إسلامه','6342','2593','0');
INSERT INTO [t96257] VALUES (2607,'في ميراث أهل الملتين بالرق، والنصراني يسلم عبده، ومن له فيه بقية رق ثم يموت ذلك العبد، أو يموت له عبد وميراث المجوس','6342','2593','0');
INSERT INTO [t96257] VALUES (2608,'في ولد المنبوذ والنفقة عليه','6343','2593','0');
INSERT INTO [t96257] VALUES (2609,'في التداعي في الولاء والإقرار فيه، وإقرار الوارث بعتق وغير ذلك من ذكر الولاء','6344','2593','0');
INSERT INTO [t96257] VALUES (2610,'فيمن مات عن زجة حامل هل يقسم ميراثه قبل أن تضع؟ أو ميت ببلد بعيد هل يقسم ورثته ميراثه؟ والمتوارثون لا يدرى أولهم موتا؟','6346','2593','0');
INSERT INTO [t96257] VALUES (2611,'فيمن مات له ولد بعد أن أنفذت مقاتله','6347','2593','0');
