INSERT INTO [t96257] VALUES (2613,'[الجزء الأول من الجنايات]','6349','2612','0');
INSERT INTO [t96257] VALUES (2614,'في القصاص بين العبيد في النفس والجراح وهل بينهم وبين الحر قصاص، وذكر جناتهم على الأحرار والعبيد','6349','2613','0');
INSERT INTO [t96257] VALUES (2615,'في العبد يجني على الحر فيفدى ثم يبرأ ما جرحه إلى أكثر منه أو يموت الحر','6352','2613','0');
INSERT INTO [t96257] VALUES (2616,'في العبد أو من فيه بقية رق يجرح عمدا ثم يموت مكانه أو بعد أن عاش أياما، أو يتنامى الجرح إلى أكثر منه، أو جرحه رجلان ثم مات، وكيف إن أقام بالجرح أو بالقتل شاهدا أو شاهدين؟','6354','2613','0');
INSERT INTO [t96257] VALUES (2617,'في الجارح أو المجروح يعتق بعد الجرح ثم يموت المجروح أو يتنامى جرحه أو يصح','6359','2613','0');
INSERT INTO [t96257] VALUES (2618,'في جنايات العبيد ودياتهم والجناية عليهم، ومن أمر رجلا بقتل عبده، ومن أخصى عبد رجل','6361','2613','0');
INSERT INTO [t96257] VALUES (2619,'في العبد ومن فيه بقية رق يقتل رجلا عمدا فعفي عنه على استرقاقه','6365','2613','0');
INSERT INTO [t96257] VALUES (2620,'في العبد يجني أو يستهلك مالا وجنايته فيما يؤتمن عليه، وجنايته على ولده أو والديه','6366','2613','0');
INSERT INTO [t96257] VALUES (2621,'في إقرار العبيد ومن فيه بقية رق بالجنايات','6368','2613','0');
INSERT INTO [t96257] VALUES (2622,'في العهد ومن فيه بقية رق يجنى وله مال، وكيف إن مات عن مال وهو عبد أو مكاتب أو أم ولد أو مدبر، أو من فيه بقية رق؟','6370','2613','0');
INSERT INTO [t96257] VALUES (2623,'في العبد ومن فيه بقية رق يجنى ثم يعتق أو يتقدم العتق جناية','6374','2613','0');
INSERT INTO [t96257] VALUES (2624,'في العبد يجني فيفديه سيده ثم يجني، ثم ينتقض جرح الأول، أو لا يفديه حتى جنى فأسلمه، أو جنى على رجلين، أو على عبد لرجلين أو عبدين','6376','2613','0');
INSERT INTO [t96257] VALUES (2625,'في العبد أو العبدين يقتلان جلا، فيعفو أحد الأولياء، أو يعفو الولي عن بعض الدية، أو يوصي بالعفو، أو يفدي من أحد الوليين حصته','6382','2613','0');
INSERT INTO [t96257] VALUES (2626,'في العهد ومن فيه بقية رق يجني على رجلين، فسلم إلى أحدهما أو يفدى، ثم قام الآخر أو لم يقم حتى جنى على ثالث','6384','2613','0');
INSERT INTO [t96257] VALUES (2627,'في العهد يجني على رجلين فيفديه السيد من أحدهما، ثم أعتقه أو باعه، ثم جرح ثالثا، ثم قام الثاني، وكيف إن باعه قبل أن يجني على الثاني ثم جنى؟','6386','2613','0');
INSERT INTO [t96257] VALUES (2628,'في الأمة تجني ثم تباع فتلد من المبتاع، ثم يعلم بذلك أو يدعي ربها أنه باعه من رجال، وقال الرجل بل زوجتنيها','6391','2613','0');
INSERT INTO [t96257] VALUES (2629,'في العبد أو من فيه بقية رق يجني على سيد أو على أحد سيديه أو عليهما، وكيف إن جنى على أجنبي مع ذلك، أو ادعى السيد أنه جرحه ثم مات','6395','2613','0');
INSERT INTO [t96257] VALUES (2630,'في العبد بين الرجلين يجني عليه أحدهما عمدا والآخر بعده أو قبله خطأ','6398','2613','0');
INSERT INTO [t96257] VALUES (2631,'في العبد يجني ثم يأبق أو يأسره العدو ثم يقع في المقاسم، وفي أم الولد تقع في المقاسم مرة بعد مرة قبل أن تفدى','6399','2613','0');
INSERT INTO [t96257] VALUES (2632,'في العبد بين الرجلين يجني أو كان لواحد فجنى على رجلين','6401','2613','0');
INSERT INTO [t96257] VALUES (2633,'في العبد بين الرجلين يجرحه عبد لهما أو لأحدهما أو يجرح عبدا بين أحدهما ورجل آخر','6401','2613','0');
INSERT INTO [t96257] VALUES (2634,'في عبدين قتلا رجلا أو جرحاه ثم قتل أحدهما صاحبه','6404','2613','0');
