INSERT INTO [t96257] VALUES (2636,'في العبد يجرح عبدا أو حرا ثم يقتله المجروح أو يقطع له عضوا','6406','2613','0');
INSERT INTO [t96257] VALUES (2637,'في العبد أو من فيه بقية رق يجني ثم يجني عليه المجني عليه أو غيره، وكيف إن جنى هو بعد ذلك؟','6407','2613','0');
INSERT INTO [t96257] VALUES (2638,'في الأمة الحامل تجني ثم جنت على ثان بعد الوضع','6413','2613','0');
INSERT INTO [t96257] VALUES (2639,'في العبد أو المدبر ومن فيه بقية رق يجني، ثم يقتله سيده','6416','2613','0');
INSERT INTO [t96257] VALUES (2640,'في العبد ومن فيه بقية رق يقتل رجلا له وليان أو رجلان لكل واحد أولياء','6417','2613','0');
INSERT INTO [t96257] VALUES (2641,'في الأمة تجني ثم تسبى ثم تغنم فتقع في المقاسم','6419','2613','0');
