INSERT INTO [t96257] VALUES (2655,'القول في ديات الأعضاء، وديات الجراح، وأسمائها، وصفاتها، وذكر ما فيه الاجتهاد منها، ومن الكسر، والضرب، والحلق','6469','2654','0');
