INSERT INTO [t96257] VALUES (2660,'دية الموضحة، والمنقلة، والمأمومة، والجائفة، وسائر شجاج الرأس، وفي الجراح، والكسر يبرأ ويعود لهيئته أو على شين','6490','2654','0');
INSERT INTO [t96257] VALUES (2661,'في دية العقل، ودية الصلب، وما تنامى من ذلك إلى غيره','6494','2654','0');
INSERT INTO [t96257] VALUES (2662,'فيمن أصيب بجرح عمدا، أو خطأ، فتنامى إلى ما هو أكثر منه، أو تنامى إلى النفس بعد قود أو عفو أو قبل ذلك، وفي تنامي جرح المستقاد منه','6495','2654','0');
INSERT INTO [t96257] VALUES (2663,'في تنامي الجاح، في العبد، والنصراني، وكيف إن أعتق هذا، وأسلم هذا، بعد التنامي إلى جرح أو نفس','6503','2654','0');
INSERT INTO [t96257] VALUES (2664,'فيمن أصاب بجراح؛ في ضربة واحدة؛ من يد، أو أيد','6509','2654','0');
INSERT INTO [t96257] VALUES (2665,'في إيقاف الحكم في الجراح إلى تناهيها، في العمد، والخطأ، وذكر ما يوقف ديته من سن الصبي وشبهه','6510','2654','0');
INSERT INTO [t96257] VALUES (2666,'في الجراح، يؤخذ عقلها ثم تبرأ، وفيمن طرحت سنه، أو أذنه، فردها، فنبتت للجاني أو المجني عليه، في العمد أو الخطأ، ونبت اللسان','6515','2654','0');
INSERT INTO [t96257] VALUES (2667,'في لسان الأخرس، وذكر الخصي، واليد الشلاء، والعين القائمة؛ تصاب، والعضو فيه الضعف؛ يصاب عمدا، أو خطأ، أو كان فيه نقص أخذ له عقلا أو لا ثم يقطع باقيه، أو يتجاوز إلى ما فيه حكومة','6519','2654','0');
INSERT INTO [t96257] VALUES (2668,'في عين الأعور، وباقي البصر','6524','2654','0');
INSERT INTO [t96257] VALUES (2669,'فيمن يضرب، فيدعي ذهاب بصره، أو سمعه، أو بعض ذلك، أو يقول ذهب مني الجماع','6527','2654','0');
INSERT INTO [t96257] VALUES (2670,'في دية المرأة ومعاقلتها الرجل فيما دون النفس إلى ثلث الدية','6529','2654','0');
INSERT INTO [t96257] VALUES (2671,'في دية أهل الكتاب، والمجوس، والمرتد','6535','2654','0');
INSERT INTO [t96257] VALUES (2672,'ما يجب في الجنين من الغرة','6537','2654','0');
INSERT INTO [t96257] VALUES (2673,'في صفة الجنين الذي تجب فيه الغرة وكيف إن استهل، والضربة عمدا أو خطأ، وميراث الغرة','6538','2654','0');
INSERT INTO [t96257] VALUES (2674,'في جنين الأمة، وأم الولد، والذمية، وكيف إن استهل، وجنين البهيمة','6543','2654','0');
INSERT INTO [t96257] VALUES (2675,'في صفة دية الخطأ، والدية المغلظة، ودية العمد إذا قبلت، وذكر ما فيه التغليظ، وذكر شبه العمد، وأسنان الإبل في ذلك كله، وأسنان الإبل في الجراح، وتغليظ دية الجرح، وما يتصل بذلك','6545','2654','0');
INSERT INTO [t96257] VALUES (2676,'[الجزء الثاني من الجراح]','6555','2653','0');
INSERT INTO [t96257] VALUES (2677,'ذكر العاقلة وكيف تحمل دية الخطأ؟ وكيف توظف عليهم؟ وهل تحمل العمد والإقرار؟','6555','2676','0');
INSERT INTO [t96257] VALUES (2678,'ذكر قول الله تعالى فيمن قتل من كافر معاهد أو مؤمن لم يهاجر، ومن قتل الآن بين الصفين، من كافر بارز غير القاتل','6563','2676','0');
INSERT INTO [t96257] VALUES (2679,'فيمن يعقل عن الذمي جرائره، وتأجيل ديته، وجناية الحربي المستأمن','6564','2676','0');
INSERT INTO [t96257] VALUES (2680,'في تأجيل الدية على العاقلة، وتأجيل دية ما دون النفس','6566','2676','0');
INSERT INTO [t96257] VALUES (2681,'فيما تحمله العاقلة من جراح الخطإ، وهل تحمل من جراح العمد شيئا؟ وهل تحمل دية الكافر؟ وجناية الصبي والمجنون والسكران وشبهه والعبيد','6567','2676','0');
INSERT INTO [t96257] VALUES (2682,'في العاقلة تؤدي شيئا تظن أنه يلزمها وهو غير لازم لها والولي يضمن عن صبي ما ظن أنه يلزمه','6572','2676','0');
INSERT INTO [t96257] VALUES (2683,'ذكر ما تحمله العاقلة من العمد الذى لا قصاص فيه، وهل تحمل العاقلة جناية المرء على نفسه؟ أو إقراره بالقتل خطأ، وذكر ما لا قصاص فيه من العمد وقد تقدم في باب ذكر العاقلة أنها لا تحمل عمدا ولا اعترافا ولا صلحا ولا عبدا','6573','2676','0');
INSERT INTO [t96257] VALUES (2684,'في كفارة القتل، وفيم إذا تجب، وذكر ما يحمل محمل الخطأ في الكفارة','6576','2676','0');
INSERT INTO [t96257] VALUES (2685,'في جناية الصبي والمجنون والنائم والسفيه والسكران، وجرح العجماء','6579','2676','0');
INSERT INTO [t96257] VALUES (2686,'فيما يحدث عن فعل الطبيب والخاتن والبيطار ومتولي القصاص والأدب والمعلم وشبه، ومن عنف في وطء امرأته فأفاضها أو قتلها أو كسرها، ومن أذهب عذرة امرأته بضربة','6583','2676','0');
INSERT INTO [t96257] VALUES (2687,'فيما أصاب الكلب العقور، والجمل الصؤول، والحائط المائل، والإبل العوادي على الزرع، وما أفسدت المواشي','6586','2676','0');
INSERT INTO [t96257] VALUES (2688,'فيمن حفر حيث لا يجوز له أو أوقف دابة أو رش فناء أو نصب سيفا أو أخرج ظلة أو ميزابا حيث لا يجوز له وما هلك بذلك هل تضمنه العاقلة؟','6591','2676','0');
INSERT INTO [t96257] VALUES (2689,'في ضمان القائد والسائق والراكب وما أصاب الدابة وحدها','6594','2676','0');
INSERT INTO [t96257] VALUES (2690,'في الفارسين أو السفينتين أو الحالمين يصطدمان فيهلكان، أو حر وعبد وفي حافري البئر يقع عليهما فيهلكان، أو يأخذ الأعلى بيد الأسفل، فيقعان جميعا','6601','2676','0');
INSERT INTO [t96257] VALUES (2691,'في السفينة تربط إلى السفينة ومن تعلق برجل في بئر أو فيما يخاف فخلاه، أو قطع الحبل، وفيمن يسقط من يده شيء أو يقع رجل على شيء فيهلك، أو يدفع أحد، فيؤتى عليه أو على من وقع عليه وشبه هذا','6603','2676','0');
INSERT INTO [t96257] VALUES (2692,'في المرمى بحجر فاتقاها فرجعت فأصابت أحدا، وفيمن كدم يد رجل فنثرها فوقع سن الكادم، ومن عبث بسقاء فسقطت عليه قلة على كتف السقاء، ومن طلب رجلا بسيف فعثر الرجل فمات','6607','2676','0');
INSERT INTO [t96257] VALUES (2693,'فيمن استعمل كبيرا أو صغيرا أو حرا أو عبدا فعطب أو عطب بسببه أحد','6608','2676','0');
