INSERT INTO [t9633] VALUES (8,'ائتني بوضوء، فتوضأ، ثم قام بفضل وضوئه، فشربه قائما، فعجبت من ذلك، فقال: أتعجب يا بني، إني','2','0','0');
INSERT INTO [t9633] VALUES (9,'لا تنكح المرأة بغير أمر وليها، فإن نكحت فنكاحها باطل، فنكاحها باطل، فنكاحها باطل، فإن','3','0','0');
INSERT INTO [t9633] VALUES (10,'غزوت مع رسول الله صلى الله عليه وسلم غزاة العسرة، وكان من أوثق أعمالي في نفسي، وكان لي','3','0','0');
INSERT INTO [t9633] VALUES (11,'رجلا زنى بامرأة فأمر به النبي صلى الله عليه وسلم فجلد الحد، ثم أخبر أنه أحصن، فأمر به','3','0','0');
INSERT INTO [t9633] VALUES (12,'سئل عن رجل يطأ الأذى والروث، فقال: إن وطئه وهو رطب فليغسله، وإن كان يابسا فلا','3','0','0');
INSERT INTO [t9633] VALUES (13,'عن بيع الصبر من الطعام لا يعلم كيلها بالكيل المسمى من التمر»','3','0','0');
