INSERT INTO [t9633] VALUES (15,'هذا سرق، فقال: لا أخاله سرق، أسرقت ويحك؟ فقال: نعم، فقال: اذهبوا به فاقطعوا يده، ثم','4','0','0');
INSERT INTO [t9633] VALUES (16,'يدخل على بعض نسائه وهي حائض، فيتكئ عليها، ويقرأ القرآن في حجرها، وتبسط له الخمرة في','4','0','0');
INSERT INTO [t9633] VALUES (17,'الله عز وجل وضع عن المسافر الصوم، وشطر الصلاة، وعن الحبلى، أو المرضع "','4','0','0');
INSERT INTO [t9633] VALUES (18,'أمرت أن أسجد على سبعة أعظم، ولا أكف شعرا ولا بنانا»','5','0','0');
INSERT INTO [t9633] VALUES (19,'{فليس عليكم جناح أن تقصروا من الصلاة إن خفتم أن يفتنكم الذين كفروا} [النساء: ١٠١] ، وقد','5','0','0');
INSERT INTO [t9633] VALUES (20,'رجل وقع بأخت امرأته، فقال ابن عباس: لعله إلى حرمه لم يحرم عليه امرأته "','5','0','0');
INSERT INTO [t9633] VALUES (21,'إن استطعتم أن لا تغدوا يوم الفطر حتى تطعموا فافعلوا»','5','0','0');
INSERT INTO [t9633] VALUES (22,'زكاة الفطر: نصف صاع من حنطة أو صاع من تمر "','5','0','0');
INSERT INTO [t9633] VALUES (23,'ما منعك أن تحجي معنا العام، قالت: يا نبي الله، إنما كان لنا ناضحان، فركب أبو فلان وابنة','5','0','0');
INSERT INTO [t9633] VALUES (24,'عودوا المرضى، واتبعوا الموتى، ولا عليكم أن تأتوا الفرس لا تخرجنكم عزمة، ولا عليكم أن لا','6','0','0');
INSERT INTO [t9633] VALUES (25,'إذا حضرت الصلاة، وحضر العشاء، فابدءوا بالعشاء، ولا تعجلوا عن عشائكم»','7','0','0');
INSERT INTO [t9633] VALUES (26,'أخبري رسول الله صلى الله عليه وسلم بما يريد أن يأكل منه، فقالوا: هو ضب، فرفع يده، فقلت:','7','0','0');
