INSERT INTO [t9633] VALUES (28,'لا يمنع أحدكم جاره أن يغرز خشبة في جداره، قال: ثم يقول أبو هريرة: ما لي أراكم عنها','8','0','0');
INSERT INTO [t9633] VALUES (29,'شر الطعام طعام الوليمة، يدعى إليه الأغنياء، ويترك الفقراء، ومن لم يأتها، فقد عصى الله','8','0','0');
