INSERT INTO [t9633] VALUES (34,'ما علمت أن اليوم الجمعة، وكنت في السوق ما زدت على الوضوء، فقال: الوضوء أيضا، ثم قال: إن','10','0','0');
INSERT INTO [t9633] VALUES (35,'أيصلي الرجل في الثوب الواحد؟ فقال رسول الله صلى الله عليه وسلم: أوكلكم له','10','0','0');
