INSERT INTO [t9633] VALUES (47,'لعبد الله بن مروان أمير المؤمنين، من عبد الله بن عمر، سلام عليك، فإني أحمد إليك الله الذي','13','0','0');
INSERT INTO [t9633] VALUES (48,'متى الساعة؟ قال: «وماذا أعددت لها؟» , قال: لا شيء والله، إني لقليل الصلاة، قليل الصيام، إلا','13','0','0');
