INSERT INTO [t9633] VALUES (63,'لولا أن أشق على أمتي لأحببت أن لا أتخلف عن سرية تخرج في سبيل الله عز وجل، ولكن لا أجد ما','16','0','0');
INSERT INTO [t9633] VALUES (64,'كنت مع رسول الله صلى الله عليه وسلم في بعض أسفاره، فسألته عن شيء فلم يجبني، ثم سألته فلم','17','0','0');
