INSERT INTO [t9633] VALUES (66,'" يطلع الله عز وجل على أهل الجنة، فيقول: يا أهل الجنة، أرضيتم، فيقولون: ما لنا لا نرضى، وقد','17','0','0');
INSERT INTO [t9633] VALUES (67,'إذا أسلم العبد، وحسن إسلامه» .','18','0','0');
INSERT INTO [t9633] VALUES (68,'من أدرك من الصبح ركعة قبل أن تطلع الشمس فقد أدرك الصبح، ومن أدرك من العصر ركعة قبل أن','18','0','0');
INSERT INTO [t9633] VALUES (69,'إذا كان أحدكم يصلي فلا يدع أحدا يمر بين يديه، وليدرأه ما استطاع، فإن أبى فليقاتله، فإنما','18','0','0');
INSERT INTO [t9633] VALUES (70,'إذا تثاءب أحدكم فليكظم ما استطاع»','18','0','0');
