INSERT INTO [t9633] VALUES (73,'لو وجدت مع امرأتي رجلا أمهله، حتى آتي بأربعة شهداء؟ قال: نعم "','19','0','0');
INSERT INTO [t9633] VALUES (74,'إذا كنت يقظانة تحدث معي بعد طلوع الفجر، وإن كنت نائمة اضطجع على شقه الأيمن حتى يأتيه','19','0','0');
INSERT INTO [t9633] VALUES (75,'إذا صلى ما بين الركعتين صلاة الفجر، فإن كنت يقظانة كلمني وجلس حتى تقام الصلاة، فيذهب','19','0','0');
INSERT INTO [t9633] VALUES (76,'يصلي إحدى عشرة ركعة، ثم يضطجع على شقه الأيمن، فإن كنت يقظانة حدثني حتى يأتيه المؤذن','20','0','0');
INSERT INTO [t9633] VALUES (77,'الله تعالى لا يقبض العلم انتزاعا ينتزعه من الناس، ولكن يقبض العلم بقبض العلماء، حتى إذا','20','0','0');
