INSERT INTO [t96484] VALUES (26,'الدليل التاسع: أن الرمي لو كان قبل الزوال في أيام التشريق جائزا، لفعله النبي - صلى الله عليه وسلم -','23','17','0');
INSERT INTO [t96484] VALUES (27,'الدليل العاشر: أن الرمي لو كان قبل الزوال جائزا؛ لبادر إليه الرسول - صلى الله عليه وسلم -','23','17','0');
INSERT INTO [t96484] VALUES (28,'الدليل الحادي عشر: أن الرسول - صلى الله عليه وسلم - بادر بالرمي حين زالت الشمس، فرمى قبل','25','17','0');
INSERT INTO [t96484] VALUES (29,'الدليل الثاني عشر: عمل جميع الصحابة بلا استثناء في حياة النبي - صلى الله عليه وسلم - وبعد مماته','25','17','0');
INSERT INTO [t96484] VALUES (30,'لم يثبت عن صحابي واحد أنه أفتى بالرمي قبل الزوال، أو رمى قبل الزوال','26','29','0');
INSERT INTO [t96484] VALUES (31,'الثالث عشر: الذي يظهر: أن الثابت عن عطاء: أنه لا يجيز الرمي قبل الزوال','27','17','0');
INSERT INTO [t96484] VALUES (32,'الرابع عشر: المحققون العلماء الربانيون','28','17','0');
INSERT INTO [t96484] VALUES (33,'* منهم هؤلاء الأئمة الأربعة أصحاب المذاهب المتبعة','28','32','0');
INSERT INTO [t96484] VALUES (34,'* وتبع هؤلاء الأئمة علماء الأمة، ولم يخالف في ذلك إلا من شذ بقوله ورأيه','28','32','0');
INSERT INTO [t96484] VALUES (35,'* قال شيخ الإسلام والمسلمين ابن تيمية رحمه الله','28','32','0');
INSERT INTO [t96484] VALUES (36,'* وقال العلامة المحقق محمد الأمين الشنقيطي رحمه الله','29','32','0');
INSERT INTO [t96484] VALUES (37,'* وقال العلامة الإمام مفتي المملكة العربية السعودية، ورئيس القضاة والشؤون الإسلامية في عصره','29','32','0');
INSERT INTO [t96484] VALUES (38,'أما الكتاب فقوله تعالى: {وما آتاكم الرسول فخذوه}','30','37','0');
INSERT INTO [t96484] VALUES (39,'وأما السنة فرميه - صلى الله عليه وسلم - بعد الزوال على وجه الامتثال والتفسير المفيد للوجوب','31','37','0');
INSERT INTO [t96484] VALUES (40,'وأما الإجماع فأمر معلوم،','31','37','0');
INSERT INTO [t96484] VALUES (41,'الخامس عشر: رمي الجمرات عبادة توقيفية في كيفيتها، وفي زمانها، ومكانها لا يجوز القول فيها بالرأي:','33','17','0');
INSERT INTO [t96484] VALUES (42,'الأمر الأول: قال الله تعالى: {قل إنما حرم ربي الفواحش ما ظهر منها وما بطن}','33','41','0');
INSERT INTO [t96484] VALUES (43,'الأمر الثاني: قال الله تعالى: {أم لهم شركاء شرعوا لهم من الدين}','33','41','0');
INSERT INTO [t96484] VALUES (44,'الأمر الثالث: عن عبد الله بن عمر رضي الله عنهما،','34','41','0');
INSERT INTO [t96484] VALUES (45,'الأمر الرابع: ذم السلف للرأي المخالف للدليل، والتحذير من القول بالرأي:','34','41','0');
INSERT INTO [t96484] VALUES (46,'١ - قال عمر بن الخطاب - رضي الله عنه -: ((إياكم وأصحاب الرأي؛ فإنهم أعداء السنن،))','34','45','0');
INSERT INTO [t96484] VALUES (47,'٢ - قال عروة بن الزبير - رضي الله عنه -: ((السنن، السنن، فإن السنن قوام الدين [أزهد الناس في]))','35','45','0');
INSERT INTO [t96484] VALUES (48,'٣ - قال سهل بن حنيف - رضي الله عنه -: ((اتهموا رأيكم، فلقد رأيتني يوم أبي جندل لو أستطيع))','35','45','0');
INSERT INTO [t96484] VALUES (49,'٤ - قال الإمام أحمد رحمه الله: ((لا تكاد ترى أحدا نظر في هذا الرأي إلا وفي))','35','45','0');
INSERT INTO [t96484] VALUES (50,'٥ - قال الأوزاعي رحمه الله: ((إذا أراد الله - عز وجل - أن يحرم عبده بركة العلم))','35','45','0');
INSERT INTO [t96484] VALUES (51,'قال جمهور أهل العلم: الرأي المذموم في الآثار المذكورة','36','50','0');
INSERT INTO [t96484] VALUES (52,'والحاصل أنه لا يجوز الاعتماد على الرأي، بل يرجع إلى الكتاب والسنة','36','50','0');
INSERT INTO [t96484] VALUES (53,'الأمر الخامس: قول العالم الرباني فيما لا يعلم: الله أعلم','37','41','0');
INSERT INTO [t96484] VALUES (54,'١ - قال عبد الله بن مسعود - رضي الله عنه -: ((يا أيها الناس، من علم شيئا فليقل به،))','37','53','0');
INSERT INTO [t96484] VALUES (55,'٢ - وقال عبد الله بن مسعود - رضي الله عنه - أيضا: من علم علما فليقل به','38','53','0');
INSERT INTO [t96484] VALUES (56,'٣ - قال عبد الله بن مسعود - رضي الله عنه - أيضا: ((إن من يفتي في كل ما يستفتونه لمجنون))','38','53','0');
INSERT INTO [t96484] VALUES (57,'٤ - سئل سعيد بن جبير عن شيء فقال: ((لا أعلم))،ثم قال: ((ويل للذي يقول لما لا يعلم: إني أعلم))','38','53','0');
INSERT INTO [t96484] VALUES (58,'٥ - قال مالك: ((ينبغي للعالم أن يألف فيما أشكل عليه قول: لا أدري؛))','38','53','0');
INSERT INTO [t96484] VALUES (59,'٦ - قال مالك: ((من أين علموا ذلك؟ قال: منك يا أبا عبد الله، فقال: ما أعلمها أنا))','38','53','0');
INSERT INTO [t96484] VALUES (60,'٧ - عن مالك رحمه الله قال: ((جنة العالم لا أدري، فإذا أغفلها أصيبت مقاتله))','38','53','0');
INSERT INTO [t96484] VALUES (61,'٨ - قال الهيثم بن جميل: سمعت مالكا سئل عن ثمان وأربعين','38','53','0');
INSERT INTO [t96484] VALUES (62,'٩ - قال خالد بن خداش: ((قدمت على مالك بأربعين مسألة، فما أجابني منها إلا في خمس مسائل))','39','53','0');
INSERT INTO [t96484] VALUES (63,'١٠ - ينبغي للعالم أن يورث جلساءه','39','53','0');
INSERT INTO [t96484] VALUES (64,'١١ - قال ابن وهب: ((لو كتبنا عن مالك: لا أدري؛ لملأنا الألواح))','39','53','0');
INSERT INTO [t96484] VALUES (65,'١٢ - عن عقبة بن مسلم أنه قال: صحبت ابن عمر أربعة وثلاثين شهرا','39','53','0');
INSERT INTO [t96484] VALUES (66,'١٣ - قال أبو داود: ((قول الرجل فيما لا يعلم: لا أعلم نصف العلم))','39','53','0');
INSERT INTO [t96484] VALUES (67,'السادس عشر: أدوار الجسور المتكررة حصل بها اليسر والتيسير','40','17','0');
