INSERT INTO [t96484] VALUES (69,'والأفضل في رمي الجمار أيام التشريق أن ترمى قبل الغروب','41','68','0');
INSERT INTO [t96484] VALUES (70,'أما الرمي بعد غروب الشمس ليلا فقد أجازه بعض أهل العلم','41','68','0');
INSERT INTO [t96484] VALUES (71,'لا بأس أن يرمي في الليل عن اليوم الذي غابت شمسه إلى آخر الليل','41','68','0');
