INSERT INTO [t96484] VALUES (85,'أما الأقوياء من الرجال والنساء فلا يجوز لهم التوكيل في الرمي','47','84','0');
INSERT INTO [t96484] VALUES (86,'وهكذا الصبي يجوز أن يرمي عنه وليه','47','84','0');
