INSERT INTO [t9793] VALUES (522,'وهذه بطون طيئ، وهو جلهمة بن أدد','479','0','0');
INSERT INTO [t9793] VALUES (523,'وهذه بطون الغوث بن طيئ: بنو ثعل بن عمرو بن الغوث؛ وبنو جرم، وهو ثعلبة بن عمرو بن الغوث؛ وبنو نبهان بن عمرو بن الغوث؛ وبنو هنى بن عمرو ابن الغوث. ولثعل بن عمرو بن الغوث بطون، وهم: بنو بحتر، ومعن، ابنى عتود بن عنين بن سلامان بن ثعل؛ وبنو سنبس بن معاوية بن ثعل؛ وبنو أخزم، والنجد، ابنى أبي أخزم بن ربيعة بن جرول بن ثعل. ولنبهان بن سعد بطنان: سعد، ونابل «٣» ، ابنا نبهان.','479','0','0');
INSERT INTO [t9793] VALUES (524,'وهذه بطون مذحج، وهو مالك بن أدد','479','0','0');
INSERT INTO [t9793] VALUES (525,'وهؤلاء بطون جلد بن مذحج: بنو رهاء «١» بن حرب بن علة بن جلد؛ وبنو صداء، وبنو جنب، وبنو يزيد بن حرب بن علة بن جلد؛ وبنو النخع بن عمرو بن علة بن جلد؛ وبنو مسلية بن عامر بن عمرو بن علة بن جلد؛ وبنو الحارث بن كعب بن عمرو بن علة بن جلد.','480','0','0');
