INSERT INTO [t9793] VALUES (531,'وهذه بطون بلى: بنو فران بن بلى؛ وبنو هنىء بن بلى.','481','0','0');
INSERT INTO [t9793] VALUES (532,'وهذه بطون جهينة: بنو رشدان؛ وبنو غطفان، ابنى قيس بن جهينة.','482','0','0');
INSERT INTO [t9793] VALUES (533,'وهذه بطون عذرة؛ بنو عامر بن عذرة؛ وبنو كاهل بن عذرة؛ وبنو كبير بن عذرة؛ وبنو حن «١» ، ورزاح، ابنى ربيعة بن حرام «٢» بن ضنة بن عبد بن كبير بن عذرة.','482','0','0');
INSERT INTO [t9793] VALUES (534,'وهذه بطون كلب بن وبرة: بنو كنانة بن بكر بن عوف بن عذرة بن زيد اللات بن رفيدة بن ثور بن كلب بن وبرة. ولبنى كنانة بطون؛ منها: بنو زهير وبنو عدى، وبنو عليم بنى جناب بن هبل بن عبد الله بن كنانة بن بكر؛ وبنو العليص بن ضمضم بن عدى بن جناب. ومن بطون بنى العليص: بنو الأخثم؛ وبنو ليلى؛ وبنو الأصبغ؛ وبنو عبيدة؛ وبنو هبل بن عبد الله بن كنانة بن بكر؛ وبنو عامر بن عوف بن بكر [بن عوف] بن عذرة بن زيد اللات بن رفيدة بن ثور بن كلب بن وبرة.','482','0','0');
