INSERT INTO [t9870] VALUES (1,'كل أمر ذي بال لا يبدأ فيه بحمد الله أقطع» فالحمد لله قبل كل مقال، وعلى كل حال، وصلي الله','1','0','0');
INSERT INTO [t9870] VALUES (2,'نضر الله عبدا سمع مقالتي فوعاها، ثم أسلمها إلى من لم يسمعها، فرب حامل فقه لا فقه له، ورب','5','0','0');
INSERT INTO [t9870] VALUES (3,'نضر الله عبدا سمع مقالتي هذه، ثم وعاها وحملها، رب حامل فقه غير فقيه، ورب حامل فقه إلى من','6','0','0');
INSERT INTO [t9870] VALUES (4,'«نضر الله عبدا سمع مقالتي فوعاها، رب حامل فقه ليس بفقيه، ورب حامل فقه إلى من هو أفقه','6','0','0');
INSERT INTO [t9870] VALUES (5,'نضر الله امرأ سمع مقالتي هذه فوعاها وحفظها وعقلها، فرب حامل فقه ليس','7','0','0');
INSERT INTO [t9870] VALUES (6,'نضر الله عبدا سمع مقالتي فوعاها , وحفظها , وبلغها , فرب حامل فقه غير فقيه، ورب حامل فقه','7','0','0');
INSERT INTO [t9870] VALUES (7,'ضمن النبي صلى الله عليه وسلم الجنة لمن أدى حديثا ترد به بدعة , وتقام به سنة، وجعل رواة','8','0','0');
INSERT INTO [t9870] VALUES (8,'من أدى إلى أمتي حديثا واحدا تقيم به سنة , وترد به بدعة، فله الجنة»','9','0','0');
INSERT INTO [t9870] VALUES (9,'اللهم ارحم خلفائي , ثلاث مرات، قيل: يا رسول الله، ومن خلفاؤك؟ قال: الذين يأتون من بعدي،','9','0','0');
INSERT INTO [t9870] VALUES (10,'{سبح لله ما في السموات وما في الأرض وهو العزيز الحكيم {١} يأيها الذين آمنوا لم تقولون ما','20','0','0');
INSERT INTO [t9870] VALUES (11,'أنزل علي خمسا، خمسا.','21','0','0');
INSERT INTO [t9870] VALUES (12,'مع كل ختمة دعوة مستجابة» ومن مستحسن ما يدعى به عند الختم ما أخبرنا به مناولة الشيخ الفقيه','22','0','0');
INSERT INTO [t9870] VALUES (13,'اللهم إني أسألك إخبات المخبتين، وإخلاص المؤمنين، ومرافقة الأبرار، واستحقاق حقائق الإيمان،','23','0','0');
INSERT INTO [t9870] VALUES (14,'أريت في منامي كأني قد عرضت على الله عز وجل، فقال لي: يا حمزة، اقرأ القرآن كما علمتك،','24','0','0');
INSERT INTO [t9870] VALUES (15,'أريت البارحة في منامي كأن القيامة قد قامت، وقي دعي بقراء القرآن، فكنت فيمن حضر، فسمعت','25','0','0');
INSERT INTO [t9870] VALUES (16,'يأتي على الناس زمان الصابر منهم على دينه كالقابض على الجمر»','103','0','0');
INSERT INTO [t9870] VALUES (17,'يأتي على الناس زمان الصابر منهم على دينه كالقابض على الجمر»','103','0','0');
INSERT INTO [t9870] VALUES (18,'" يأتي على الناس زمان الصابر منهم على دينه له أجر خمسين منكم، قال: يا رسول الله، خمسين منا؟','103','0','0');
INSERT INTO [t9870] VALUES (19,'" حدثوا عن الأشراف فإنهم لا يرضون أن يدنسوا شرفهم بالكذب والخيانة وللعالم أبي العباس العزفي','123','0','0');
INSERT INTO [t9870] VALUES (20,'أفضل ما أدعو الله به؟ فقال له: في الستر في الدنيا، وأن يميتك على الإسلام، والله عز وجل','157','0','0');
INSERT INTO [t9870] VALUES (21,'لي خمسة أسماء، وأنا محمد، وأنا أحمد، وأنا الماحي الذي يمحو الله بي الكفر، وأنا الحاشر','157','0','0');
INSERT INTO [t9870] VALUES (22,'أنا محمد، وأنا أحمد، وأنا الماحي الذي يمحو الله بي الكفر، وأنا الحاشر الذي أحشر الناس،','158','0','0');
INSERT INTO [t9870] VALUES (23,'أنا محمد، وأنا أحمد، وأنا الماحي الذي يمحى بي الكفر، وأنا الحاشر الذي أحشر الناس، وأنا','159','0','0');
INSERT INTO [t9870] VALUES (24,'عقلت من النبي صلى الله عليه وسلم مجه مجها في وجهي، وأنا ابن خمس سنين من دلو» . وترجم','192','0','0');
INSERT INTO [t9870] VALUES (25,'من يأتي بني قريظة فيأتيني بخبرهم، فانطلقت، فلما رجعت جمع لي النبي صلى الله عليه وسلم','194','0','0');
INSERT INTO [t9870] VALUES (26,'يحدث عن جده يعقوب بن إسماعيل بن حماد بحديث لقنه منه، وهو ابن أربع سنين وروي عن العباس بن','194','0','0');
INSERT INTO [t9870] VALUES (27,'كنت ابن ثلاث سنين وكنت أقوم بالليل أنظر إلى صلاة خالي من محمد بن سوار، وكان يقوم الليل،','195','0','0');
INSERT INTO [t9870] VALUES (28,'قال لي خالي يوما: أولا تذكر الله الذي خلقك؟ فقلت: كيف أذكره؟ فقال: قل بقلبك عند تقلبك في','195','0','0');
INSERT INTO [t9870] VALUES (29,'صبيا ابن أربع سنين قد حمل إلى المأمون، قد قرأ القرآن ونظر في الرأي، غير أنه إذ جاع','196','0','0');
INSERT INTO [t9870] VALUES (30,'الكبر، الكبر، فقال الشاب: يا أمير المؤمنين لو كان الأمر بالسن، لكان في المسلمين من هو أسن','196','0','0');
INSERT INTO [t9870] VALUES (31,'إذا كان العبد متزوجا لأمة فأصابها العتق» . الحديث عن الشيخ الوجيه أمير الحاج عماد الدين أبي','198','0','0');
INSERT INTO [t9870] VALUES (32,'أقسم بالله وآياته ... ومشعر الخيف ومرقاته إن الحريري حري بأن ... تكتب بالتبر مقاماته جزء','280','0','0');
